import React, { useState, useEffect } from 'react';
import _HeaderMovieDetails from './_HeaderMovieDetails';
import _HeaderMovieCast from './_HeaderMovieCast';
import shaw from '../../Static/Images/shaw.png';
import narcos from '../../Static/Images/narcos.png'

import {
    Button, Container, Menu, Responsive, Segment,
    Visibility, Grid, Divider, Image, Header, Embed, Search, Icon, Dropdown, Label
}
    from 'semantic-ui-react'

const _Header = ({ Imagess }) => {

    const [bgColor, setBgColor] = useState('#111111');
    const [width, setWidth] = useState(window.innerWidth);

    useEffect(() => {
        const handleResize = () => setWidth(window.innerWidth);
        window.addEventListener('resize', handleResize);
        if (width < 1000)
            setBgColor('transparent')
        else
            setBgColor('#111111')

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    });



    return (
        <React.Fragment>
            <Grid.Row className="customGridPadding">
                <Grid.Column width={4}>
                    <center>
                        <br />
                        <Image src={Imagess.post} className="sizeOfTopRatedSearchMovie" />
                    </center>
                </Grid.Column>

                <Grid.Column width={12} className="headerImageBoxShadow"
                    style={{
                        width: '100%',
                        minHeight: 400,
                        maxHeight: 400,
                        backgroundImage: `url(${Imagess.bgImg})`,
                        display: 'inline-block',
                        backgroundSize: 'cover',
                        display: 'inline-block'
                    }}>
                    <Grid className="customGridPadding">
                        <Grid.Column width={12}>
                            <Segment style={{ backgroundColor: 'transparent', minHeight: 400, maxHeight: 400 }}>
                                <Header color="green" style={{ fontSize: '3.5em', fontFamilty: '', fontWeight: 'bold', padding: '1rem' }}>
                                    {Imagess.name}
                                </Header>
                                <p style={{ margin: '1rem' }} >
                                    <_HeaderMovieDetails url="https://www.youtube.com/watch?v=AGv_F9hpQ-w" />
                                </p>
                                <Responsive as='p' minWidth={1000} className="customPara" style={{ marginTop: '2rem' }}>
                                   {Imagess.desc}
                            </Responsive>
                            </Segment>
                        </Grid.Column>
                    </Grid>
                </Grid.Column>
            </Grid.Row>
        </React.Fragment>
    )
}

export default _Header;
