import React, { useReducer, useEffect, useState } from 'react';
import _AxiosInstance from '../Store/_AxiosInstance'
import DisplayAllGeresOfMovies from '../Containers/DisplayAllGeresOfMovies';
import { Grid, Image } from 'semantic-ui-react'

const initState = {
    movieList: [],
    movieOverview: {},
    classNameForImage: '',
    ImageClassName: ''
}

const reducder = (prevState, action) => {
    switch (action.type) {
        case 'FETCH_TRENDING':
            return {
                movieList: action.payload.data.results,
                classNameForImage: 'scrollmenuImage',
                ImageClassName: 'sizeOfContainerMovieImage'
            };

        case 'FETCH_HORROW':
            return {
                movieList: action.payload.data.results,
                classNameForImage: 'scrollmenuImageBigger',
                ImageClassName: 'sizeOfContainerMovieImageBigger'
            };

        default:
            return prevState;
    }
}

const _Redux = props => {

    const [stateTrending, dispatchTrending] = useReducer(reducder, initState);
    const [movieOverview, setMovieOverview] = useState({});
    const [yes, setYes] = useState(false);

    let movies;

    useEffect(() => {
        _AxiosInstance.get('/trending/all/week?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US')
            .then(data => {
                dispatchTrending({ type: props.FetchType, payload: data })
            })
            .catch(err => {

            })
    }, []);

    if (stateTrending.movieList) {
        movies = getMovieRows(stateTrending.movieList, stateTrending.ImageClassName);
    }

    return (
        <React.Fragment>
            <Grid className="GridRow">
                <Grid.Column className="GridColumn">
                    <div className={stateTrending.classNameForImage}>
                        {
                            <center className="customImageDisplay">
                                {movies}
                            </center>
                        }
                    </div>
                </Grid.Column>
            </Grid>
        </React.Fragment>

    )
}

const getMovieRows = (movies, ImageClass) => {
    const movieRow = movies.map((movie) => {
        let movieImageUrl = 'https://image.tmdb.org/t/p/w500/' + movie.backdrop_path;

        if (movie.poster_path && movie.backdrop_path !== null) {
            const movieComponent = (
                <DisplayAllGeresOfMovies
                    movieDetials={selectedMovie(movie)}
                    ImgClass={ImageClass}
                    key={movie.id}
                    movie={movie}
                />
            );

            return movieComponent;
        }
    });
    return movieRow;
}

const selectedMovie = movie => {
    let url;
    let urlVideo;
    let details = {};
    if (movie.media_type === "movie") {
        const movieId = movie.id;
        console.log(movieId)
        url = `https://api.themoviedb.org/3/movie/${movieId}?api_key=028e8ea4ca7d9164d085c9238979098b&append_to_response=videos`;

    } else if (movie.media_type === "tv") {
        const tvId = movie.id
        url = `https://api.themoviedb.org/3/tv/${tvId}?api_key=028e8ea4ca7d9164d085c9238979098b&append_to_response=videos`;
    }

    axios.get(url)
        .then(res => {
            const movieData = res.data;
            setMovieOverview(movieData);

        }).catch(error => {
            console.log(error);
        });
}


export default _Redux;
