import React, { useState, useEffect } from 'react';
import {
    Button, Container, Menu, Responsive, Segment,
    Visibility, Grid, Divider, Image, Header, Search, Icon, Dropdown, Label
} from 'semantic-ui-react'

import _HeaderMovieCast from './_HeaderMovieCast';
import _HeaderMovieDetails from '../Desktop/_HeaderMovieDetails';
import axios from 'axios';
import _Header from '../Desktop/_Header'


const _NavigationItems = () => {
    const [movieList, setMovieList] = useState([]);
    const [Image, setImage] = useState({ bgImg: '', post: '', name: '', desc: '', youtube: '' });
    const [YouTube, setYoutube] = useState();

    const geTYouTube = id => {
        
        axios.get(`https://api.themoviedb.org/3/movie/${id}?api_key=028e8ea4ca7d9164d085c9238979098b&append_to_response=videos`)
            .then(x => {
                console.log("xxxxxxxxxxxxxxxx", x)
                const videos = x.data.videos;
                console.log(videos);
                const Trailer = videos.results.sort((a, b) => b.size - a.size).filter(x => x.type === 'Trailer');
                const Teaser = videos.results.sort((a, b) => b.size - a.size).filter(x => x.type === 'Teaser');
                console.log("xxxxxxxxxxxxxxxxTrailer ", Trailer)
                console.log("xxxxxxxxxxxxxxxx Teaser", Teaser)
                let key = '';
                if (Trailer)
                    key = Trailer.map(x => x.key)
                else
                    key = Teaser.map(x => x.key)

                console.log(key);
                setYoutube(`https://www.youtube.com/watch?v=${key}`);
                console.log("youtube key", YouTube);
            })
            .catch(err => {

            })
    }

    useEffect(() => {

        const url = 'https://api.themoviedb.org/3/trending/movie/week?api_key=028e8ea4ca7d9164d085c9238979098b&include_adult=false';
        axios.get(url)
            .then(res => {
                const results = res.data.results;
                let movieImageUrll;
                let postImageUrl;
                let name = '';
                let movieRows = [];

                movieImageUrll = "https://image.tmdb.org/t/p/w500" + results[0].backdrop_path;
                postImageUrl = "https://image.tmdb.org/t/p/w500" + results[0].poster_path;
                geTYouTube(results[0].id);
                setImage({
                    bgImg: movieImageUrll,
                    post: postImageUrl,
                    name: results[0].title || results[0].name,
                    desc: results[0].overview
                });

                results.forEach((movie) => {
                    let movieImageUrl;
                    movieImageUrl = "https://image.tmdb.org/t/p/w500" + movie.backdrop_path;
                    if (movie.poster_path !== null && movie.media_type !== "person") {
                        const movieComponent = <_HeaderMovieCast
                            movieDetials={() => selectMovieHandler(movie)}
                            key={movie.id}
                            movieImage={movieImageUrl}
                            movie={movie}
                        />
                        movieRows.push(movieComponent);
                    }
                })

                setMovieList(movieRows);

            }).catch(error => {
                console.log("errror", error);
            });

        return () => {
            setMovieList([]);
            console.log("cleaned DC")
        }

    }, []);

    const selectMovieHandler = movie => {
        let url;
        let urlVideo;
        let details = {};
        if (movie.media_type === "movie") {
            const movieId = movie.id;
            console.log(movieId)
            url = `https://api.themoviedb.org/3/movie/${movieId}?api_key=028e8ea4ca7d9164d085c9238979098b&append_to_response=videos`;

        } else if (movie.media_type === "tv") {
            const tvId = movie.id
            url = `https://api.themoviedb.org/3/tv/${tvId}?api_key=028e8ea4ca7d9164d085c9238979098b&append_to_response=videos`;
        }

        axios.get(url)
            .then(res => {
                let movieImageUrl = "https://image.tmdb.org/t/p/w500" + res.data.backdrop_path;
                let moviePoster = "https://image.tmdb.org/t/p/w500" + res.data.poster_path;
                let desc = res.data.overview;
                geTYouTube(res.data.id);
                setImage({ bgImg: movieImageUrl, post: moviePoster, desc: res.data.overview, name: res.data.title || res.data.name });

            }).catch(error => {
                console.log(error);
            });
    }

    return (
        <React.Fragment>
            <_Header Imagess={Image} />
            <Grid style={{ display: 'contents' }}>
                <Grid.Column width={4}>
                    &nbsp;
                    </Grid.Column>
                <Grid.Column width={12}>
                    <_HeaderMovieDetails url={YouTube} />
                </Grid.Column>
            </Grid>
            <Grid.Row>
                <Grid.Column width={1}>&nbsp;</Grid.Column>
                <Grid.Column width={14} textAlign="center" className="scrollmenuImageBigger">
                    {movieList}
                </Grid.Column>
                <Grid.Column width={1}>&nbsp;</Grid.Column>
            </Grid.Row>
        </React.Fragment>
    )
}

export default _NavigationItems;
