import React, { useReducer } from 'react';


const initStateListOfMovies = {
    listOfMovies: [],
    backdrop_path: '',
    id: 0,
    poster_path: '',
    original_name: '',
    overview: ''
}

const reducer = (prevState, {field, value}) => {
    
    return {
        [field] : value
    }
}


const _Redux = () => {

    const [newState, dispatch] = useReducer(reducer, initStateListOfMovies)

    return (
        <div>
            <input type='text' name={newState.overview} onChange={e => dispatch({ field : e.target.field, value: e.target.value })} />
            <input type='text' name={newState.backdrop_path} onChange={e => dispatch({ field: e.target.field, value: e.target.value })} />
            {newState.overview} | {newState.backdrop_path}
        </div>
    )
}

export default _Redux;
