import React, { useState, useEffect } from 'react';
import { Header, Icon, Segment, Grid, Modal, Search, Button, Embed, Image, Rating, Divider } from 'semantic-ui-react';
import _HeaderMovieCast from './_HeaderMovieCast';
import ReactPlayer from 'react-player'


const _SearchMovieList = props => {
    const [open, setOpen] = useState(true);
    const [defHeight, setHeight] = useState(window.innerWidth);
    const [YouTube, setYoutube] = useState('https://www.youtube.com/watch?v=');

    useEffect(() => {
        const setHeightofScreen = () => setHeight(window.innerHeight)
        window.addEventListener('resize', setHeightofScreen)

        return () => {
            window.removeEventListener('resize', setHeightofScreen)
        }
    })

    useEffect(() => {
        const setHeightofScreen = () => setHeight(window.innerHeight)
        window.addEventListener('resize', setHeightofScreen)

        return () => {
            window.removeEventListener('resize', setHeightofScreen)
        }
    },[props])

    const close = () => {
        props.onCloseModal();
        setOpen(false);
    }

    return (
        <React.Fragment>
            <Modal size={'fullscreen'} dimmer="blurring" open={open} onClose={() => close()}>
                <Modal.Actions style={{ padding: '0rem', backgroundColor: '#000000' }}>
                    <Grid padded textAlign="center">
                        <Grid.Column width={13} >
                            <Search
                                value={props.searchInputValue}
                                showNoResults={false}
                                className="customSearch"
                                onSearchChange={props.onSearchHandler}
                                placeholder="Search..." />
                        </Grid.Column>
                        <Grid.Column width={3}>
                            <Segment color='#000000' style={{ padding: '10px', margin: '0px' }}>
                                <Button onClick={() => close()} icon labelPosition='left' style={{ backgroundColor: `#111111` }}>
                                    <Icon name='checkmark' color='green' />
                                    <span style={{ color: 'white' }}>Done
                                            </span>
                                </Button>
                            </Segment>
                        </Grid.Column>
                    </Grid>
                </Modal.Actions>
                <Modal.Content style={{ backgroundColor: 'transparent !important' }}>
                    <Modal.Description>
                        <Grid>
                            <Grid.Column width={6}>
                                <Modal.Content scrolling style={{ backgroundColor: 'transparent !important', minHeight: `${defHeight}px` }}>
                                    <Modal.Description>
                                        <Grid padded>
                                            <Grid.Row>
                                                <Grid.Column textAlign='center'>
                                                    {props.listOfMovies}
                                                </Grid.Column>
                                            </Grid.Row>
                                        </Grid>

                                    </Modal.Description>
                                </Modal.Content>

                            </Grid.Column>
                            <Grid.Column width={10}>

                                <Segment color='black' padded style={{borderTop:'0px solid green !important;'}}>
                                    <ReactPlayer url={YouTube}
                                        loop
                                        width='100%'
                                        controls
                                    />
                                </Segment>
                                <Grid>
                                    <Grid.Row>
                                        <Grid.Column width={4}>
                                            <Segment verticalAlign='middle'>
                                                <Header color="green" style={{ fontSize: '2em', fontFamilty: '', fontWeight: 'bold', padding: '1rem' }}>
                                                    {props.movie.title || props.movie.name}
                                                </Header>
                                            </Segment>

                                        </Grid.Column >
                                        <Grid.Column width={12}>
                                            <Segment>
                                                {props.movie.overview}
                                            </Segment>
                                            <Segment>
                                                Rating: {props.movie.vote_average * 10}%{" "}
                                                <p>
                                                    {props.movie.number_of_episodes ? " Episodes: " + props.movie.number_of_episodes : ""}
                                                    {props.movie.number_of_seasons ? " Seasons: " + props.movie.number_of_seasons : ""}
                                                </p>
                                                Release date: {props.movie.release_date || props.movie.first_air_date}  Runtime: {props.movie.runtime || props.movie.episode_run_time}m
          
                                            </Segment>
                                            <Segment>
                                                <Button icon labelPosition='left' style={{ backgroundColor: `#111111` }}>
                                                    <Icon name='dollar' color='green' />
                                                    <span style={{ color: 'white' }}>Buy
                                            </span>
                                                </Button>
                                                <Divider />
                                            </Segment>
                                        </Grid.Column>
                                    </Grid.Row>
                                </Grid>
                            </Grid.Column>
                        </Grid>
                    </Modal.Description>
                </Modal.Content>

            </Modal>
        </React.Fragment >
    )
}

export default _SearchMovieList;
