import GetWidthOfComponent from './GetWidthOfComponent'
import React, { Component, useState, useEffect, useRef } from 'react'
import _Header from '../SecondaryComponents/Desktop/_Header'
import _NavigationItems from '../SecondaryComponents/Desktop/_NavigationItems'
import _MovieGenres from '../SecondaryComponents/Desktop/_MovieGenres'
import axios from 'axios'
import _Movie from '../../src/Store/_Movie'
import _SearchMovieList from '../SecondaryComponents/Desktop/_SearchMovieList'

import { Responsive, Grid } from 'semantic-ui-react'
import _BrowseCategories from '../SecondaryComponents/Desktop/_BrowseCategories'
import _MovieGenresList from '../SecondaryComponents/Desktop/_MovieGenresList'

const DesktopComponent = ({ children }) => {

  const [inputValue, setInputValue] = useState('');
  const [movieList, setMovieList] = useState([]);
  const [movieOverview, setMovieOverview] = useState({});

  useEffect(() => {

    const url = `https://api.themoviedb.org/3/search/multi?api_key=9ea839ec7891591994ec0f540b4b199f&language=en-US&include_adult=false&query=${inputValue}`;
    axios.get(url)
      .then(res => {
        const results = res.data.results;
        let movieImageUrl;
        let movieRows = [];
        
        results.forEach((movie) => {
          if (movie.poster_path !== null && movie.media_type !== "person") {
            // console.log("     d fd f df d fd fd f df  ")
            movieImageUrl = "https://image.tmdb.org/t/p/w500" + movie.poster_path;

            const movieComponent = <_SearchMovie
              movie={movie}
              onSearchHandler={e => setInputValue(e.target.value)}
              searchInputValue={inputValue}
              onCloseModal={() => setInputValue('')}
            // movieDetails={() => selectMovieHandler(movie)}
            // key={movie.id}
            // movieImage={movieImageUrl}
            // movie={movie} 
            />

            movieRows.push(movieComponent);
            console.log("response each movie", movieComponent);
          }
         
        })
        console.log("response each movie", movieRows);
        setMovieList(movieRows);

      }).catch(error => {
        console.log("errror", error);
      });

    return () => {
      setMovieList([]);
      console.log("cleaned DC")
    }

  }, [inputValue]);


  const selectMovieHandler = (movie) => {
    let url;
    if (movie.media_type === "movie") {
      const movieId = movie.id;
      url = `https://api.themoviedb.org/3/movie/${movieId}?api_key=224ce27b38a3805ecf6f6c36eb3ba9d0`;

    } else if (movie.media_type === "tv") {
      const tvId = movie.id
      url = `https://api.themoviedb.org/3/tv/${tvId}?api_key=224ce27b38a3805ecf6f6c36eb3ba9d0`;
    }

    axios.get(url)
      .then(res => {
        const movieData = res.data;
        setMovieOverview(movieData);

      }).catch(error => {
        console.log(error);
      });
  }

  console.log("this is outside", movieList)

  return (
    <Responsive getWidth={GetWidthOfComponent} minWidth={Responsive.onlyTablet.minWidth}>

      <Grid>
        <_Header />
        <_NavigationItems />
      </Grid>

      <_BrowseCategories />

      <_MovieGenres onSearchHandler={e => setInputValue(e.target.value)} />

      {/* {inputValue ? <_SearchMovieList onSearchHandler={e => setInputValue(e.target.value)}  listOfMovies={movieList} searchInputValue={inputValue} onCloseModal={() => setInputValue('')}/> : <_MovieGenresList />} */}

      {/* <_MovieGenresList /> */}

      {/* {children} */}
      {/* <div>{movieList}</div> */}

    </Responsive >
  )
}


const _SearchMovie = (props) => {
  console.log("called")
  return (
    <div>
      {props.movie}
    </div>
  )
}
export default DesktopComponent;
