import React from 'react'
import { Image } from 'semantic-ui-react'

const DisplayAllGeresOfMovies = props => {
    let classNameToAppend ='';
    if(props.ITypeMovie === 'movie')
        classNameToAppend = 'sizeOfNewSearchMovie';
    else
        classNameToAppend = 'sizeOfNewSearchTv';
    return (
        <React.Fragment>
            <center className="customImageDisplay">
               <Image inline onClick={() => props.movieDetials(props.ITypeMovie)} src={`https://image.tmdb.org/t/p/original/${props.movie.poster_path}`}
                    className={classNameToAppend}
                />
            </center>
        </React.Fragment>

    )
}
export default DisplayAllGeresOfMovies;
