import HomepageHeading from '../HomePageHeading/HomepageHeading';
import getWidth from '../ContainerWidth/GetWidth';
import React, { Component, useState } from 'react'
import { Button, Container, Icon, Menu, Responsive, Segment, Sidebar, Visibility } from 'semantic-ui-react';

const MobileContainer = ({ children }) => {

  const [sidebarOpened, setSidebarOpened] = useState(false);
  const [fixed, setFixed] = useState(false);

  const handleToggle = () => {
    setSidebarOpened(!sidebarOpened)
  }

  return (

    <Responsive as={Sidebar.Pushable} getWidth={getWidth} maxWidth={Responsive.onlyMobile.maxWidth}>

      <Sidebar vertical as={Menu} animation='overlay' inverted onHide={() => handleToggle()} visible={sidebarOpened}>
        <Menu.Item as='a'>Home</Menu.Item>
      </Sidebar>

      <Sidebar.Pusher dimmed={sidebarOpened}>
        <Segment inverted textAlign='center' style={{ minHeight: 300, padding: '0em' }} vertical>
          <Container>
            <Menu inverted pointing secondary size='small'>
              <Menu.Item onClick={() => handleToggle()}>
                <Icon name='sidebar' />
              </Menu.Item>
              {/* <Menu.Item position='right'>
                <Button as='a' inverted>
                  Log in
                </Button>
              </Menu.Item> */}
            </Menu>
          </Container>
          <HomepageHeading bool />
        </Segment>
      </Sidebar.Pusher>
      {children}
    </Responsive >

  )
}

export default MobileContainer;
