import React, { Component, useState, useEffect, useRef, useReducer } from 'react'
import _Header from '../SecondaryComponents/Desktop/_Header'
import _NavigationItems from '../SecondaryComponents/Desktop/_NavigationItems'
import _MovieGenres from '../SecondaryComponents/Desktop/_MovieGenres'
import axios from 'axios'
import _SearchMovieList from '../SecondaryComponents/Desktop/_SearchMovieList'
import _BrowseCategories from '../SecondaryComponents/Desktop/_BrowseCategories'
import _SearchMovieListDisplay from '../SecondaryComponents/Desktop/_SearchMovieListDisplay'



const SearchGenresResult = ({ movieId, type, name }) => {
  
  const [ignored, forceUpdate] = useReducer(x => x + 1, 0);
  const [inputValueNew, setTheNewInputValue] = useState('');
  const [movieList, setMovieList] = useState([]);
  const [movieOverview, setMovieOverview] = useState({});
  const [value, setValue] = useState(false);

  const typeInput = e => {
    setTheNewInputValue(e.target.value);
  }

  const closeModalAgain = () => {
    setTheNewInputValue('');
    forceUpdate();
  }

  useEffect(() => {
    const url = `https://api.themoviedb.org/3/search/multi?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US&include_adult=false&query=${inputValueNew}`;
    axios.get(url)
      .then(res => {
        const results = res.data.results;
        let movieImageUrl;
        let movieRows = [];
        results.forEach((movie) => {
          if (movie.poster_path !== null && movie.media_type !== "person") {
            movieImageUrl = "https://image.tmdb.org/t/p/w500" + movie.poster_path;

            const movieComponent = <_SearchMovieListDisplay
              movieDetials={() => selectMovieHandler(movie)}
              key={movie.id}
              movieImage={movieImageUrl}
              movie={movie}
            />
            movieRows.push(movieComponent);
            // console.log(movie)
          }
        })

        setMovieList(movieRows);


      }).catch(error => {
        console.log("errror", error);
      });

    return () => {
      setMovieList([]);
      console.log("cleaned DC")
    }

  }, [inputValueNew]);


  const Url = x => `https://api.themoviedb.org/3/movie/${x}?api_key=028e8ea4ca7d9164d085c9238979098b&append_to_response=videos`;
  const UrlTv = x => `https://api.themoviedb.org/3/tv/${x}?api_key=028e8ea4ca7d9164d085c9238979098b&append_to_response=videos`;


  const callUrl = url => {
    axios.get(url)
      .then(res => {
        const movieData = res.data;
        setMovieOverview(movieData);

      }).catch(error => {
        console.log(error);
      });
  }

  useEffect(() => {
    let url;
    if (type === "movie")
      url = Url(movieId)

    else if (type === "tv")
      url = UrlTv(movieId)

    callUrl(url);
    setTheNewInputValue(name);
    setValue(true);

  }, [name]);


  const selectMovieHandler = movie => {
    let url;
    if (movie.media_type === "movie")
      url = Url(movie.id)

    else if (movie.media_type === "tv")
      url = UrlTv(movie.id)

    callUrl(url);
  }

  console.log("MOVIE ID", movieId);
  console.log("MOVIE OVERVIEW", movieOverview);
  console.log("INPUT VALUE", inputValueNew);
  console.log("VALUE", value);

  return (
    <div>
      {value ? <_SearchMovieList movie={movieOverview} listOfMovies={movieList} onSearchHandler={typeInput} searchInputValue={inputValueNew} onCloseModal={closeModalAgain} /> : " "}
    </div>
  )

}


export default SearchGenresResult;
