import React, { useState, useEffect } from 'react';
import { Header, Icon, Segment, Grid, Modal, Search, Button, Embed, Image, Rating, Divider } from 'semantic-ui-react';
import _HeaderMovieCast from './_HeaderMovieCast';


const _SearchMovieList = props => {
    const [open, setOpen] = useState(true);
    const [searchValue, setSearchValue] = useState('');


    useEffect(() => {
        console.log(props.searchInputValue)
    });

    const close = () => {
        props.onCloseModal();
        setOpen(false);
    }

    return (
        <React.Fragment>
            <Modal dimmer={true} open={open} onClose={() => close()}>
                <Modal.Content style={{ backgroundColor: 'transparent !important' }}>
                    <Modal.Description>
                        <Grid>
                            <Grid.Row>
                                <Grid.Column>
                                    <Search
                                        value={props.searchInputValue}
                                        showNoResults={false}
                                        className="customSearch"
                                        onSearchChange={props.onSearchHandler}
                                        placeholder="Search..." />
                                </Grid.Column>
                            </Grid.Row>
                            <Grid.Row>
                                <Grid.Column width={12}>
                                    {props.listOfMovies}
                                </Grid.Column>
                            </Grid.Row>
                        </Grid>

                    </Modal.Description>
                </Modal.Content>
                <Modal.Actions style={{ padding: '0rem', backgroundColor: '#000000' }}>
                    <Segment color='#000000'>
                        <Button onClick={() => close()} icon labelPosition='left' style={{ backgroundColor: `#111111` }}>
                            <Icon name='checkmark' color='red' />
                            <span style={{ color: 'white' }}>Done
                                            </span>
                        </Button>
                        <Divider hidden />
                    </Segment>
                </Modal.Actions>
            </Modal>
        </React.Fragment >
    )
}

export default _SearchMovieList;
