import HomepageHeading from '../HomePageHeading/HomepageHeading';
import getWidth from '../ContainerWidth/GetWidth';
import React, { Component, useState } from 'react'
import { Button, Container, Icon, Menu, Responsive, Segment, Sidebar,} from 'semantic-ui-react';

const MobileContainer= ({children}) => {
  
    const[sidebarOpened, setSidebarOpened] = useState();

    const handleToggle = () =>{
      setSidebarOpened(!sidebarOpened)
    }

      return (
        <Responsive
          as={Sidebar.Pushable}
          getWidth={getWidth}
          maxWidth={Responsive.onlyMobile.maxWidth}
        >
          <Sidebar
            as={Menu}
            animation='push'
            inverted
            onHide={() => handleToggle()}
            vertical
            visible={sidebarOpened}
          >
            <Menu.Item as='a' active>Home</Menu.Item>
            <Menu.Item as='a'>Work</Menu.Item>
            <Menu.Item as='a'>Company</Menu.Item>
            <Menu.Item as='a'>Careers</Menu.Item>
            <Menu.Item as='a'>Log in</Menu.Item>
            <Menu.Item as='a'>Sign Up</Menu.Item>
          </Sidebar>
  
          <Sidebar.Pusher dimmed={sidebarOpened}>
            <Segment
              inverted
              textAlign='center'
              style={{ minHeight: 300, padding: '1em' }}
              vertical
            >
              <Container>
                <Menu inverted pointing secondary size='small'>
                  <Menu.Item onClick={() => handleToggle()}>
                    <Icon name='sidebar' />
                  </Menu.Item>
                  <Menu.Item position='right'>
                    <Button as='a' inverted>
                      Log in
                    </Button>
                    <Button as='a' inverted style={{ marginLeft: '0.5em' }}>
                      Sign Up
                    </Button>
                  </Menu.Item>
                </Menu>
              </Container>
              <HomepageHeading bool />
            </Segment>
            {children}
          </Sidebar.Pusher>
        </Responsive>
      )
    }

  export default MobileContainer;
