import React, { useState, useEffect } from 'react';
import abc from '../../Static/Images/abc.png'
import { Segment, Grid, Image, Header, Icon, Input, Container, List }
    from 'semantic-ui-react'
import _RegistrationModal from './_RegistrationModal';

const _Header = () => {
    const [value, setValue] = useState(false);

    const showModal = () => {
        setValue(true);
    }

    const CloseModal = () => {
        setValue(false);
    }

    return (
        <React.Fragment>
            <Grid.Row className="customGridPadding">
                <Grid.Column width={16}
                    style={{
                        width: '100%',
                        minHeight: 440,
                        maxHeight: 440,
                        backgroundImage: `url(${abc})`,
                        display: 'inline-block',
                        backgroundSize: 'cover',
                    }}>
                    <Container clearing style={{ backgroundColor: 'transparent', width: "100%", borderBottom: '1px solid transparent', padding: '30px 50px 0px 50px' }}>
                        <div style={{ float: 'left' }}>
                            <List bulleted horizontal >
                                <List.Item as='a' style={{ color: '#fff' }}>Find a Counsellor</List.Item>
                                <List.Item as='a' style={{ color: '#fff' }}>Become a Counsellor</List.Item>
                            </List>
                        </div>
                        <div style={{ float: 'right' }}>
                            <Icon.Group size='large'>
                                <Icon name='question circle' />
                            </Icon.Group>

                            <span onClick={() => showModal()} className="LogIn" >
                                <Icon.Group size='large' style={{ marginLeft: '20px' }}>
                                    <Icon name='sign-in' />
                                </Icon.Group>&nbsp;&nbsp;Log In
                            </span>
                        </div>
                    </Container>

                    <div className="customGridPaddingMainSearch">
                        <Grid>
                            <Grid.Row>
                                <Grid.Column width={10}>
                                    <Input
                                        size='huge'
                                        action={{
                                            color: 'teal',
                                            labelPosition: 'left',
                                            icon: 'address book outline',
                                            content: 'Find a course',
                                        }}
                                        actionPosition='right'
                                        placeholder='Search your courses..'
                                        defaultValue=''
                                    />
                                </Grid.Column>
                                <Grid.Column>
                                        <Image src=""/>
                                </Grid.Column>
                            </Grid.Row>
                        </Grid>

                    </div>
                </Grid.Column>
            </Grid.Row>

            {value ? <_RegistrationModal onCloseModal={() => CloseModal()} /> : " "}

        </React.Fragment>
    )
}

export default _Header;
