import React, { useReducer, useEffect } from 'react';
import axios from 'axios'

const initState = {
    movieList: [],
    movieOverview: {}
}

const reducder = (prevState, action) => {
    switch (action.type) {
        case 'FETCH_TRENDING':
            console.log("2", action.payload.data.results)
            return {
                ...prevState,
                movieList: action.payload.data.results
            };

        default:
            return prevState;
    }
}

const _Redux = () => {

    const [state, dispatch] = useReducer(reducder, initState);

    useEffect(() => {
        axios.get('https://api.themoviedb.org/3/trending/all/week?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US')
            .then(data => {
                console.log("1st one", data);
                dispatch({ type: 'FETCH_TRENDING', payload: data })
            })
            .catch(err => {

            })
    });

     console.log(state.movieList);

    return (
        <h1>
            {state.movieList.map(x => (
                <h1>{x.media_type}</h1>
            ))}
        </h1>
    )

}

export default _Redux;
