import React, { useState, useEffect } from 'react';
import { Header, Icon, Segment, Grid, Modal, Button, Embed, Image, Rating, Divider } from 'semantic-ui-react';
import ImageOfRolePlayers from '../../../src/HeaderSegement/Explore/ImageOfRolePlayers';
import ReactPlayer from 'react-player'


const MovieInfo = (props) => {
    const [open, setOpen] = useState(false);
    const [dimmer, setDimmer] = useState('');
    const [url, setUrl] = useState(props.url);
    const [bgColor, setBgColor] = useState('#000000');
    const [width, setWidth] = useState(window.innerWidth);

    useEffect(() => {
        const handleResize = () => setWidth(window.innerWidth);
        window.addEventListener('resize', handleResize);
        if (width < 1000)
            setBgColor('#111111')
        else
            setBgColor('#000000')
        return () => {
            window.removeEventListener('resize', handleResize);
        };
    });


    const show = (dimmer) => {
        setDimmer(dimmer);
        setOpen(true);
    }

    const close = () => {
        setOpen(false);
    }

    return (
        <React.Fragment>
            <Modal trigger={
                <Button icon labelPosition='left' style={{ backgroundColor: `${bgColor}` }} onClick={() => show('blurring')}>
                    <Icon name='play' color='green' />
                    <span style={{ color: 'white' }}>Play
                    </span>
                </Button>} dimmer={dimmer} open={open} onClose={() => close()}>
                <Modal.Content style={{ backgroundColor: 'transparent !important' }}>
                    <Modal.Description>
                        <Segment color='transparent' inverted>
                            <ReactPlayer url={url}
                                playing
                                loop
                                width='100%'
                                controls
                            />
                        </Segment>
                        <Grid>
                            <Grid.Row>
                                <Grid.Column width={4}>
                                    <Segment verticalAlign='middle'>
                                        <Header color="green" style={{ fontSize: '3.5em', fontFamilty: '', fontWeight: 'bold', padding: '1rem' }}>
                                            Narcos
                                        </Header>
                                    </Segment>
                                </Grid.Column >
                                <Grid.Column width={12}>
                                    <Segment>
                                        Narcos: Mexico is the story of Mexico’s first drug kingpin, Miguel Ángel Félix Gallardo (Diego Luna). The 10 episodes that premiere this week detail the dramatic implosion of Gallardo’s empire, a collapse that makes for extremely bingeable television. Yet, despite the thrilling spectacle, exhaustion seeps in. Even though it aims at being something more, Narcos: Mexico doesn’t seem to have ambitions far beyond those of the criminals it follows, pushing more product.
                                    </Segment>
                                    <Segment>
                                        <Rating icon='star' defaultRating={5} maxRating={5} />
                                    </Segment>
                                    <Segment>
                                        <Button icon labelPosition='left' style={{ backgroundColor: `#111111` }}>
                                            <Icon name='dollar' color='green' />
                                            <span style={{ color: 'white' }}>Buy
                                            </span>
                                        </Button>
                                        <Divider />
                                    </Segment>

                                </Grid.Column>
                            </Grid.Row>
                        </Grid>

                    </Modal.Description>
                </Modal.Content>
                <Modal.Actions style={{ padding: '0rem', backgroundColor: 'black' }}>
                    <Segment color='transparent' inverted>
                        <Button
                            color='violet'
                            icon='checkmark'
                            labelPosition='right'
                            content="Done"
                            onClick={() => close()}
                        />
                    </Segment>
                </Modal.Actions>
            </Modal>
        </React.Fragment >
    )
}

export default MovieInfo;
