import React, { useReducer, useEffect } from 'react';
import _AxiosInstance from '../Store/_AxiosInstance'
import _MovieGenresList from '../SecondaryComponents/Desktop/_MovieGenresList';

const initState = {
    movieList: [],
    movieOverview: {}
}

const reducder = (prevState, action) => {
    switch (action.type) {
        case 'FETCH_TRENDING':
            return {
                ...prevState,
                movieList: action.payload.data.results
            };

        default:
            return prevState;
    }
}

const _Redux = () => {

    const [state, dispatch] = useReducer(reducder, initState);

    useEffect(() => {
        _AxiosInstance.get('/trending/all/week?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US')
            .then(data => {
                dispatch({ type: 'FETCH_TRENDING', payload: data })
            })
            .catch(err => {

            })
    }, []);

    let movies;
    if (state.movieList) {
        movies = getMovieRows(state.movieList, '/trending/all/week?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US')
    }

    return (
        <React.Fragment>
            {movies}
        </React.Fragment>

    )
}


const getMovieRows = (movies, url) => {
    const movieRow = movies.map((movie) => {
        let movieImageUrl = 'https://image.tmdb.org/t/p/w500/' + movie.backdrop_path;
        movieImageUrl = 'https://image.tmdb.org/t/p/original/' + movie.poster_path;

        if (movie.poster_path && movie.backdrop_path !== null) {
            const movieComponent = (
                <_MovieGenresList
                    key={movie.id}
                    url={url}
                    posterUrl={movieImageUrl}
                    movie={movie}
                />
            );

            return movieComponent;
        }
    });

    return movieRow;
}

export default _Redux;
