import HomepageHeading from '../HomePageHeading/HomepageHeading';
import getWidth from '../ContainerWidth/GetWidth'
import React, { Component, useState } from 'react'
import { Button, Container, Menu, Responsive, Segment, Visibility } from 'semantic-ui-react'


const DesktopContainer = ({children}) => {
    
  const[ fixed, setFixed] = useState();

  const FixedMenu = () => {
    setFixed(!fixed)
  }

  return (
    <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
      <Visibility
        once={false}
        onBottomPassed={() => FixedMenu()}
        onBottomPassedReverse={() => FixedMenu()}
      >
        <Segment
          inverted
          textAlign='center'
          style={{ minHeight: 450, padding: '1em' }}
          vertical
        >
          <Menu
            fixed={fixed ? 'top' : null}
            inverted={!fixed}
            pointing={!fixed}
            secondary={!fixed}
            size='small'
          >
            <Container>
              <Menu.Item as='a' active>Home</Menu.Item>
              <Menu.Item as='a'>Work</Menu.Item>
              <Menu.Item as='a'>Company</Menu.Item>
              <Menu.Item as='a'>Careers</Menu.Item>
              <Menu.Item position='right'>
                <Button as='a' inverted={!fixed}>
                  Log in
              </Button>
              </Menu.Item>
            </Container>
          </Menu>
          <HomepageHeading />
        </Segment>
      </Visibility>
      {children}
    </Responsive>
  )
}

export default DesktopContainer;
