import React, { useState, useEffect, useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Label, Input, Loader, Divider, List, Dropdown, Select
} from 'semantic-ui-react';
import { useForm } from "react-hook-form";
import { useStep } from 'react-hooks-helper';
import 'react-image-crop/dist/ReactCrop.css'
import ReactCrop from 'react-image-crop';
import { Redirect } from 'react-router-dom';

const Registration_Sixth = ({ formData, setForm, navigation }) => {

    const { COUNSELLOR_DOCUMENT } = formData;
    const [selectedFile, setSelectFile] = useState(null);



    const { handleSubmit, register, errors } = useForm({

    });

    const onSubmit = () => {

        formData.COUNSELLOR_DOCUMENT = selectedFile;
        // const data = new FormData()
        // for (var x = 0; x < selectedFile.length; x++) {
        //     data.append('file', selectedFile[x])
        // }

        navigation.next()
    };


    const onChangeHandler = (event) => {
        setSelectFile(event.target.files);

    }

    console.log(selectedFile)

    return (

        <Grid>
            <Grid.Column>
                <center>
                    <Segment stacked style={{ width: '80%', textAlign: 'left' }}>
                        <h2>Profile Photo</h2>
                        <center>
                            <Grid>
                                <Grid.Row>
                                    <Grid.Column width={16}>
                                        <Segment stacked style={{ width: '100%' }}>
                                            <div style={{ width: '100%', textAlign: 'left' }}>
                                                <Label color='blue' horizontal>
                                                    Select your Files
                                            </Label>
                                            </div>
                                            <br />

                                            <Button as="label" htmlFor="file" type="button">
                                                Some button stuff
                                            </Button>
                                            <input type="file" id="file" style={{ display: 'none' }} onChange={onChangeHandler} />
                                            {
                                                selectedFile ?

                                                    selectedFile.map(element => {
                                                        return(
                                                        <div id="upload-box" style={{ color: 'red' }}>
                                                            <p>Filename: {element.name}</p>
                                                            <p>File type: {element.type}</p>
                                                        </div>
                                                        )
                                                    })

                                                    :

                                                    ""
                                            }
                                        </Segment>

                                    </Grid.Column>
                                </Grid.Row>
                            </Grid>
                        </center>
                        <br /><br />
                        <Form size='small' onSubmit={handleSubmit(onSubmit)}>

                            <Button
                                color='teal'
                                fluid
                                size='large'
                                onClick={() => navigation.previous()}
                            >
                                Back
                            </Button>
                            <br />
                            <Button
                                color='teal'
                                fluid
                                size='large'
                                type="submit"

                            >
                                Next
                    </Button>
                        </Form>
                    </Segment>
                </center>

            </Grid.Column>
        </Grid >
    )

}

export default Registration_Sixth;
