import React from 'react'
import { useForm, Error } from "react-hook-form";
import { useHistory } from "react-router-dom";
import { useStateMachine } from "little-state-machine";
import __updateAction from '../RegistrationComponents/__updateAction';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Divider, List
} from 'semantic-ui-react';


const __RegistrationForm1 = () => {

    const { state, action } = useStateMachine(__updateAction);

    const { handleSubmit, errors, register } = useForm({
        defaultValues: state.yourDetails
    });

    const { push } = useHistory();

    const onSubmit = (data) => {
        action(data);
        push("/__RegistrationForm2");
    };


    return (
        <Grid textAlign='center' verticalAlign='middle'>
            <Grid.Column style={{ maxWidth: 450 }}>
                <Header as='h2' color='black' textAlign='center' style={{ padding: "10px" }}>
                    Registration
                </Header>
                <br />
                <Divider horizontal>Or</Divider>

                <form onSubmit={handleSubmit(onSubmit)}>
                    <h2>Step 1</h2>
                    <label>
                        First Name:
                        <input
                            name="firstName"
                            ref={register({ required: "This is required." })}
                        />
                        <ErrorMessage errors={errors} name="firstName" as="p" />
                    </label>
                    <label>
                        Last Name:
                    <input
                            name="lastName"
                            ref={register({ required: "This is required." })}
                        />
                        {/* <ErrorMessage errors={errors} name="lastName" as="p" /> */}
                    </label>
                    <input type="submit" />
                </form>

            </Grid.Column>
        </Grid>
    )
}

export default __RegistrationForm1;
