import React, { useState, useEffect} from 'react';
import { Header, Icon, Segment, Grid, Modal, Button, Embed, Image } from 'semantic-ui-react';
import ReactPlayer from 'react-player'


const MovieInfo = (props) => {
    const [open, setOpen] = useState(false);
    const [dimmer, setDimmer] = useState('');
    const [url, setUrl] = useState(props.url);
    const [bgColor, setBgColor] = useState('#000000');
    const [width, setWidth] = useState(window.innerWidth);

    useEffect(() => {
      const handleResize = () => setWidth(window.innerWidth);
      window.addEventListener('resize', handleResize);
      if (width < 1000)
        setBgColor('#111111')
      else
        setBgColor('#000000')
      return () => {
        window.removeEventListener('resize', handleResize);
      };
    });


    const show = (dimmer) => {
        setDimmer(dimmer);
        setOpen(true);
    }

    const close = () => {
        setOpen(false);
    }

    return (
        <React.Fragment>
                <Modal trigger={<Button icon labelPosition='left' style={{backgroundColor:`${bgColor}`}} onClick={() => show('blurring')}><Icon name='play' color='green' /><span style={{color:'white'}}>Play</span></Button>} dimmer={dimmer} open={open} onClose={() => close()}>
                    <Segment color='transparent' inverted>
                        <ReactPlayer url={url}
                            playing
                            loop
                            width='100%'
                            controls
                        />
                    </Segment>
                    <Modal.Actions>
                        <Button
                            positive
                            icon='checkmark'
                            labelPosition='right'
                            content="Ok"
                            onClick={() => close()}
                        />
                    </Modal.Actions>
                </Modal>
        </React.Fragment>
    )
}

export default MovieInfo;
