import React, { useState, useEffect } from 'react';
import { Header, Icon, Segment, Grid, Modal, Search, Button, Dimmer, Loader, Embed, Image, Rating, Divider } from 'semantic-ui-react';
import _HeaderMovieCast from './_HeaderMovieCast';
import ReactPlayer from 'react-player'


const _SearchMovieList = props => {
    const [open, setOpen] = useState(true);
    const [defHeight, setHeight] = useState(window.innerWidth);
    const [YouTube, setYoutube] = useState('');

    useEffect(() => {
        const setHeightofScreen = () => setHeight(window.innerHeight)
        window.addEventListener('resize', setHeightofScreen)

        return () => {
            window.removeEventListener('resize', setHeightofScreen)
        }
    })

    useEffect(() => {

        if (props.movie.videos) {

            const Trailer = props.movie.videos.results.sort((a, b) => b.size - a.size).filter(x => x.type === 'Trailer');
            console.log(Trailer);
            const Teaser = props.movie.videos.results.sort((a, b) => b.size - a.size).filter(x => x.type === 'Teaser');
            console.log(Teaser);
            let key = 'ybji16u608U';
            if (Trailer)
                key = Trailer.map(x => x.key)
            else
                key = Teaser.map(x => x.key)

            setYoutube(`https://www.youtube.com/watch?v=${key}`);
        }


        return () => {
            setYoutube('');
        }
    }, [props.movie])


    const close = () => {
        props.onCloseModal();
        setOpen(false);
    }

    return (
        <React.Fragment>
            <Modal size={'fullscreen'} dimmer="blurring" open={open} onClose={() => close()}>
                <Modal.Actions style={{ padding: '0rem', backgroundColor: '#000000' }}>
                    <Grid padded textAlign="center">
                        <Grid.Column width={13} >
                            <Search
                                value={props.searchInputValue}
                                showNoResults={false}
                                className="customSearch"
                                onSearchChange={props.onSearchHandler}
                                placeholder="Search..." />
                        </Grid.Column>
                        <Grid.Column width={3}>
                            <Segment color='#000000' style={{ padding: '10px', margin: '0px' }}>
                                <Button onClick={() => close()} icon labelPosition='left' style={{ backgroundColor: `#111111` }}>
                                    <Icon name='checkmark' color='green' />
                                    <span style={{ color: 'white' }}>Done
                                            </span>
                                </Button>
                            </Segment>
                        </Grid.Column>
                    </Grid>
                </Modal.Actions>
                <Modal.Content style={{ backgroundColor: 'transparent !important' }}>
                    <Modal.Description>
                        <Grid style={{ marginRight: '1rem' }}>
                            <Grid.Column width={6}>
                                <Modal.Content scrolling style={{ marginRight: '0rem', backgroundColor: 'transparent !important', minHeight: `${defHeight}px` }}>
                                    <Modal.Description>
                                        <Grid padded>
                                            <Grid.Row>
                                                <Grid.Column textAlign='center'>
                                                    {props.listOfMovies}
                                                </Grid.Column>
                                            </Grid.Row>
                                        </Grid>

                                    </Modal.Description>
                                </Modal.Content>

                            </Grid.Column>
                            <Grid.Column width={10}>
                                <Segment color='green' padded textAlign="center">
                                    {YouTube ? <ReactPlayer url={YouTube}
                                        loop
                                        width='100%'
                                        controls
                                    /> :
                                        <div>
                                            <Dimmer active>
                                                <Loader padded indeterminate style={{ fontSize: '18px' }}>Click Videos to Play</Loader>
                                            </Dimmer>
                                            <Icon name="youtube" style={{ fontSize: '20em' }} color="red" />
                                        </div>
                                    }
                                </Segment>
                                {props.movie.overview ?
                                    <Grid style={{ marginRight: '1rem' }}>
                                        <Grid.Row >
                                            <Grid.Column>
                                                <Segment verticalAlign='middle' style={{ backgroundColor: '#111111' }}>
                                                    <Header color="green" style={{ fontSize: '2.5em', fontFamilty: '', fontWeight: 'bold', padding: '1rem' }}>
                                                        {props.movie.title || props.movie.name}
                                                    </Header>
                                                </Segment>
                                            </Grid.Column>
                                        </Grid.Row>
                                        <Grid.Row>
                                            <Grid.Column width={16}>
                                                <Segment style={{ backgroundColor: '#111111' }}>
                                                   {props.movie.overview ? 
                                                    <Grid>
                                                        <Grid.Column width={4}>
                                                            <Icon name="info" color="green"></Icon> Description
                                                        </Grid.Column>
                                                        <Grid.Column width={12} style={{textAlign:'justify'}}>
                                                            {props.movie.overview}
                                                        </Grid.Column>
                                                    </Grid> : "" }
                                                    {props.movie.vote_average ? 
                                                    <Grid>
                                                        <Grid.Column width={4}>
                                                            <Icon name="star" color="green"></Icon>  Rating
                                                        </Grid.Column>
                                                        <Grid.Column width={12}>
                                                            {props.movie.vote_average * 10}%{" "}
                                                        </Grid.Column>
                                                    </Grid> : "" }
                                                    {props.movie.number_of_episodes ?
                                                    <Grid>
                                                        <Grid.Column width={4}>
                                                            <Icon name="braille" color="green"></Icon> Episodes
                                                        </Grid.Column>
                                                        <Grid.Column width={12}>
                                                            {props.movie.number_of_episodes}
                                                        </Grid.Column>
                                                    </Grid> : ""}
                                                    {props.movie.number_of_seasons ?
                                                    <Grid>
                                                        <Grid.Column width={4}>
                                                            <Icon name="folder open" color="green"></Icon> Seasons
                                                        </Grid.Column>
                                                        <Grid.Column width={12}>
                                                            {props.movie.number_of_seasons}
                                                        </Grid.Column>
                                                    </Grid> : "" }
                                                    {props.movie.release_date || props.movie.first_air_date ? 
                                                    <Grid>
                                                        <Grid.Column width={4}>
                                                            <Icon name="cog" color="green"></Icon> Release Date
                                                        </Grid.Column>
                                                        <Grid.Column width={12}>
                                                            {props.movie.release_date || props.movie.first_air_date}
                                                        </Grid.Column>
                                                    </Grid> : "" }
                                                    {props.movie.runtime || props.movie.episode_run_time ?
                                                    <Grid>
                                                        <Grid.Column width={4}>
                                                            <Icon name="hourglass half" color="green"></Icon> Runtime
                                                        </Grid.Column>
                                                        <Grid.Column width={12}>
                                                        {props.movie.runtime || props.movie.episode_run_time}
                                                        </Grid.Column>
                                                    </Grid> : "" }
                                                    <br/>
                                                </Segment>
                                            </Grid.Column>
                                        </Grid.Row>
                                    </Grid> : <div></div>}
                            </Grid.Column>
                        </Grid>
                    </Modal.Description>
                </Modal.Content>

            </Modal>
        </React.Fragment >
    )
}

export default _SearchMovieList;
