                        {props.movie.number_of_seasons ? " Seasons: " + props.movie.number_of_seasons : ""}
                    </p>
                    <p>{props.movie.overview}</p> */}

            <List bulleted horizontal>
                <center className="customImageDisplay">
                    <Image onClick={props.movieDetails} src={props.movieImage} className="sizeOfNewSearchMovie" />
                     Rating: {props.movie.vote_average * 10}%{" "}
                </center>
            </List >
            }
        </React.Fragment >
    )
}

export default _SearchMovieListDisplay;
