import React, { useState, useEffect, useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Label, Loader, Progress, Divider, List, Dropdown, Select
} from 'semantic-ui-react';
import { useForm } from "react-hook-form";
import { useStep } from 'react-hooks-helper';
import demoPicture from '../../../Static/Images/demoPicture.png'
import amazingPhoto from '../../../Static/Images/amazingPhoto.png'
import 'react-image-crop/dist/ReactCrop.css'
import ReactCrop from 'react-image-crop';

const Registration_Second = ({ formData, setForm, navigation }) => {

    const { COUNSELLOR_DOCUMENT_IMAGE } = formData;
    const [src, selectFile] = useState(null);
    const [image, setImage] = useState(null);
    const [crop, setCrop] = useState({ aspect: 1 / 1 });
    const [result, setResult] = useState(null);
    const [completed, setCompleted] = useState(0);


    const { handleSubmit, register, errors } = useForm({

    });

    const onSubmit = (data) => {

        navigation.next()
    };

    const handleFileChange = e => {
        selectFile(URL.createObjectURL(e.target.files[0]))
        getCroppedImg();
    }

    function getCroppedImg() {
        const canvas = document.createElement('canvas');
        const base64Image = canvas.toDataURL('image/jpeg');
        setResult(base64Image);

    }

    const Cropped = () => {
        const e = {
            target: {
                name: "COUNSELLOR_DOCUMENT_IMAGE",
                value: result
            }
        };
        setForm(e);
    };


    useEffect(() => {
        const e = {
            target: {
                name: "COUNSELLOR_DOCUMENT_IMAGE",
                value: result
            }
        };
        setForm(e);
    }, [result])


    useEffect(() => {
        setInterval(() => setCompleted(Math.floor(Math.random() * 100) + 1), 2000);
      }, []);



    console.log(result);
    console.log(formData);

    return (

        <Grid>
            <Grid.Column>
                <center>
                    <Segment stacked style={{ width: '80%', textAlign: 'left' }}>
                        <h2>Profile Photo</h2>
                        <center>
                            <Grid>
                                <Grid.Row>
                                    <Grid.Column width={16}>
                                        <Segment stacked style={{ width: '100%' }}>
                                            <div style={{ width: '100%', textAlign: 'left' }}>
                                                <Label color='blue' horizontal>
                                                    Select your Photo and crop it
                                            </Label>
                                            </div>
                                            <br />
                                            <label class="custom-file-input" for="Upload" >
                                            </label>
                                            <br />
                                            <input
                                                type="file"
                                                onChange={handleFileChange}
                                                id="Upload"
                                                name="_photos"
                                                accept="image/*"
                                                className="hiddenButton"
                                            />
                                        </Segment>
                                        <Segment>
                                            <Progress percent={10} size='tiny'>
                                             </Progress>
                                             {result && "Document Uploaded"}
                                           
                                        </Segment>
                                    </Grid.Column>
                                </Grid.Row>
                            </Grid>
                        </center>
                        <br /><br />
                        <Form size='small' onSubmit={handleSubmit(onSubmit)}>

                            <Button
                                color='teal'
                                fluid
                                size='large'
                                onClick={() => navigation.previous()}
                            >
                                Back
                            </Button>
                            <br />
                            <Button
                                color='teal'
                                fluid
                                size='large'
                                type="submit"
                                onClick={() => navigation.next()}
                            >
                                Next
                    </Button>
                        </Form>
                    </Segment>
                </center>

            </Grid.Column>
        </Grid >
    )

}

export default Registration_Second;
