import React, { useReducer, useEffect, useState } from 'react';
import axios from 'axios';
import _Movie from './_Movie';


// const initStateListOfMovies = {
//     toggleMovieList: true,
//     MovieList: [],
//     toggleModal: false,
//     movieOverview: {},
// }

// const reducer = (prevState, action) => {

//     switch (action.type) {
//         case "success":
//             return (
//                 {
//                     ...prevState,
//                     listOfMovies: action.payload
//                 }
//             )
//     }
// }


const _Redux = () => {

    const [toggleMovieList, setToggleMovieList] = useState(true);
    const [MovieList, setMovieList] = useState([]);
    const [toggleModal, setToggleModal] = useState(false);
    const [movieOverview, setMovieOverview] = useState({});

    const makeAipCall = (searchItem) => {
        const url = `https://api.themoviedb.org/3/search/multi?api_key=9ea839ec7891591994ec0f540b4b199f&language=en-US&include_adult=false&query=${searchItem}`;

        axios.get(url)
            .then(res => {
                const results = res.data.results;
                let movieImageUrl;
                let movieRows = [];

                results.forEach((movie) => {
                    if (movie.poster_path !== null && movie.media_type !== "person") {
                        movieImageUrl = "https://image.tmdb.org/t/p/w500" + movie.poster_path;

                        const movieComponent = <_Movie
                            movieDetails={() => selectMovieHandler(movie)}
                            key={movie.id}
                            movieImage={movieImageUrl}
                            movie={movie} />

                        movieRows.push(movieComponent);
                    }
                })
                setMovieList(movieRows);

            }).catch(error => {
                console.log(error);
            });
    }

    const onSearchHandler = (event) => {
        setToggleMovieList(false);

        const userInput = event.target.value;
        makeAipCall(userInput);

        if (userInput === "")
            setToggleMovieList(true)
    }


    const selectMovieHandler = (movie) => {
        setToggleModal(true);

        let url;
        if (movie.media_type === "movie") {
            const movieId = movie.id;
            url = `https://api.themoviedb.org/3/movie/${movieId}?api_key=224ce27b38a3805ecf6f6c36eb3ba9d0`;

        } else if (movie.media_type === "tv") {
            const tvId = movie.id
            url = `https://api.themoviedb.org/3/tv/${tvId}?api_key=224ce27b38a3805ecf6f6c36eb3ba9d0`;
        }

        axios.get(url)
            .then(res => {
                const movieData = res.data;

                setMovieOverview(movieData);
            }).catch(error => {
                console.log(error);
            });

    }

    const closeModal = () => {
        setToggleModal(false);
    }



    return (
        <div>
            <input
                onChange={onSearchHandler}
                type="text"
                placeholder="Title, genres, people" />
            {
                MovieList
            }
            <Modal show={toggleModal}
                modalClosed={closeModal}
                movie={movieOverview}>

                <MovieDetails movie={movieOverview} />
            </Modal>
        </div>
    )
}


const Modal = (props) =>{
    const backgroundStyle = {
       backgroundSize: "cover",
       backgroundImage: `url(https://image.tmdb.org/t/p/original/${props.movie.backdrop_path || props.movie.poster_path})`,
    }
 
    return (
          <div
             style={backgroundStyle}
             className={(props.show ? "modal show" : "modal hide")}>
             {props.children}
          </div>
    )
 }

export default _Redux;
