import React, { useState, useEffect } from 'react'
import _AxiosInstance from '../../Store/_AxiosInstance'
import { Grid, Image } from 'semantic-ui-react'

const _MovieGenresList = () => {

    const [movieList, setMovieList] = useState([]);


    useEffect(() => {

        _AxiosInstance.get('discover/movie?api_key=224ce27b38a3805ecf6f6c36eb3ba9d0&with_genres=28')
            .then(x => {
                setMovieList(
                    x.data.results.map(x => {
                        return { value: x.backdrop_path }
                    })
                )
            })
            .catch(err => {
                console.log(err)
            })

            return () => {
                setMovieList([]);
                console.log("cleaned")
            }
    }, []);

    // console.log(movieList)
    return (
        <React.Fragment>
            <Grid className="GridRow">
                <Grid.Column className="GridColumn">
                    <div className="scrollmenuImage">
                        {
                            movieList.map(x =>
                                <center className="customImageDisplay">
                                    <Image inline src={`https://image.tmdb.org/t/p/original/${x.value}`}
                                        className="sizeOfContainerMovieImage"
                                    />
                                </center>
                            )
                        }

                    </div>
                </Grid.Column>
            </Grid>
            <Grid className="GridRow">
                <Grid.Column className="GridColumn">
                    <div className="scrollmenuImageBigger">
                        {
                            movieList.map(x =>
                                <center className="customImageDisplay">
                                    <Image inline src={`https://image.tmdb.org/t/p/original/${x.value}`}
                                        className="sizeOfContainerMovieImageBigger"
                                    />
                                </center>
                            )
                        }

                    </div>
                </Grid.Column>
            </Grid>
            <Grid className="GridRow">
                <Grid.Column className="GridColumn">
                    <div className="scrollmenuImageBigger">
                        {
                            movieList.map(x =>
                                <center className="customImageDisplay">
                                    <Image inline src={`https://image.tmdb.org/t/p/original/${x.value}`}
                                        className="sizeOfContainerMovieImageBigger"
                                    />
                                </center>
                            )
                        }

                    </div>
                </Grid.Column>
            </Grid>
            
        </React.Fragment>

    )
}

export default _MovieGenresList
