import React, { useState, useEffect } from 'react';
import { Header, Icon, Segment, Grid, Modal, Search, Button, Dimmer, Loader } from 'semantic-ui-react';
import ReactPlayer from 'react-player'


const _RegistrationModal = props  => {
    const [open, setOpen] = useState(true);
    const [defHeight, setHeight] = useState(window.innerWidth);

    useEffect(() => {
        const setHeightofScreen = () => setHeight(window.innerHeight)
        window.addEventListener('resize', setHeightofScreen)

        return () => {
            window.removeEventListener('resize', setHeightofScreen)
        }
    })


    const close = () => {
        setOpen(false);
        props.onCloseModal();
    }

    return (
        <React.Fragment>
            <Modal size={'fullscreen'} dimmer="blurring" open={open} onClose={() => close()}>
                <Modal.Actions style={{ padding: '0rem', backgroundColor: '#000000' }}>
                    <Grid padded="true" textAlign="center">
                        <Grid.Column width={13} >
                            <Search
                                value={props.searchInputValue}
                                showNoResults={false}
                                className="customSearch"
                                onSearchChange={props.onSearchHandler}
                                placeholder={props.defName} />
                        </Grid.Column>
                        <Grid.Column width={3}>
                            <Segment style={{ padding: '10px', margin: '0px' }}>
                                <Button onClick={() => close()} icon labelPosition='left' style={{ backgroundColor: `#111111` }}>
                                    <Icon name='checkmark' color='green' />
                                    <span style={{ color: 'white' }}>Done
                                            </span>
                                </Button>
                            </Segment>
                        </Grid.Column>
                    </Grid>
                </Modal.Actions>
                <Modal.Content style={{ backgroundColor: 'transparent !important' }}>
                    <Modal.Description>
                        <Grid style={{ marginRight: '1rem' }}>
                            <Grid.Column width={6}>
                                <Modal.Content scrolling style={{ marginRight: '0rem', backgroundColor: 'transparent !important', minHeight: `${defHeight}px` }}>
                                    <Modal.Description>
                                        <Grid padded="true">
                                            <Grid.Row>
                                                <Grid.Column textAlign='center'>
                                                    {props.listOfMovies}
                                                </Grid.Column>
                                            </Grid.Row>
                                        </Grid>

                                    </Modal.Description>
                                </Modal.Content>

                            </Grid.Column>
                           
                        </Grid>
                    </Modal.Description>
                </Modal.Content>

            </Modal>
        </React.Fragment >
    )
}

export default _RegistrationModal;
