import React, { useReducer, useEffect } from 'react';
import _AxiosInstance from '../Store/_AxiosInstance'
import _MovieGenresListTrending from '../SecondaryComponents/Desktop/_MovieGenresListTrending';
import { Grid, Image } from 'semantic-ui-react'
import _MovieGenresListHorror from '../SecondaryComponents/Desktop/_MovieGenresListHorror';

const initState = {
    movieList: [],
    movieOverview: {},
    classNameForImage: ''
}

const reducder = (prevState, action) => {
    switch (action.type) {
        case 'FETCH_TRENDING':
            return {
                ...prevState,
                movieList: action.payload.data.results,
                classNameForImage : 'scrollmenuImage'
            };

        case 'FETCH_HORROW':
            return {
                ...prevState,
                movieList: action.payload.data.results,
                classNameForImage : 'scrollmenuImageBigger'
            };

        default:
            return prevState;
    }
}

const _Redux = props => {

    const [stateTrending, dispatchTrending] = useReducer(reducder, initState);
    let movies;

    useEffect(() => {
        alert();
        _AxiosInstance.get('/trending/all/week?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US')
            .then(data => {
                dispatchTrending({ type: props.FetchType, payload: data })
            })
            .catch(err => {

            })
      },[]);

    if (stateTrending.movieList) {
        movies = getMovieRows(stateTrending.movieList);
    }

    return (
        <React.Fragment>
            <Grid className="GridRow">
                <Grid.Column className="GridColumn">
                    <div className="scrollmenuImage">
                        {
                            <center className={stateTrending.classNameForImage}>
                                {movies}
                            </center>
                        }
                    </div>
                </Grid.Column>
            </Grid>
        </React.Fragment>

    )
}

const getMovieRows = (movies) => {
    const movieRow = movies.map((movie) => {
        let movieImageUrl = 'https://image.tmdb.org/t/p/w500/' + movie.backdrop_path;

        if (movie.poster_path && movie.backdrop_path !== null) {
            const movieComponent = (
                <_MovieGenresListTrending

                    //movieDetials = {selectedMovie(movie)}
                    key={movie.id}
                    movie={movie}
                />
            );

            return movieComponent;
        }
    });


    return movieRow;
}

export default _Redux;
