import React, { useState, useEffect, useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Divider, List
} from 'semantic-ui-react';

import  { RegistrationContext } from './_RegistrationComponent';

const Registration_First = () => {

    const Regis_Context = useContext(RegistrationContext)

    const Reg_Form_1 = e => {
        Regis_Context.Regis_Dispatch({ type: "registration_first", field: e.target.name, value: e.target.value })
    }

    return (
        <Grid textAlign='center' verticalAlign='middle'>
            <Grid.Column style={{ maxWidth: 450 }}>
               
               <input type='text' name="name" value={Regis_Context.Regis_State.name} onChange={Reg_Form_1} />

                <Header as='h2' color='black' textAlign='center' style={{ padding: "10px" }}>
                    Registration
                    </Header>
                <br />
                <Divider horizontal>Or</Divider>
                <Form size='small'>
                    <Segment stacked>
                        <Form.Input fluid icon='user' iconPosition='left' placeholder='E-mail address' />
                        <Form.Input
                            fluid
                            icon='lock'
                            iconPosition='left'
                            placeholder='Password'
                            type='password'
                        />

                        <Button color='teal' fluid size='large'>
                            Login
                        </Button>
                    </Segment>
                </Form>

            </Grid.Column>
        </Grid>
    )

}

export default Registration_First;
