import React, { useState, useEffect, useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Divider, List, Dropdown, Select
} from 'semantic-ui-react';
import { useForm } from "react-hook-form";
import { useStep } from 'react-hooks-helper';
import demoPicture from '../../../Static/Images/demoPicture.png'
import amazingPhoto from '../../../Static/Images/amazingPhoto.png'
import AvatarEditor from 'react-avatar-editor';
import ImageCrop from '../../../MainComponents/Containers/ImageCropper'

const Registration_Second = ({ formData, setForm, navigation }) => {

    const { COUNSELLOR_PHOTO } = formData;
    const [proPicture, setProPicture] = useState({ userProfilePic: '', editor: null, scaleValue: 1 });

    const { handleSubmit, register, errors } = useForm({

    });

    const setEditorRef = e => {
        setProPicture({editor: e })
    }

    const onSubmit = (data) => {

        navigation.next()
    };
    
    const profileImageChage = fileChangeEvent => {
        const file = fileChangeEvent.targe.files[0];
        const { type } = file;
        if(type.endsWith('jpeg') || type.endsWith('png') || type.endsWith('jpg'))
        {
            setProPicture({
                selectedImage : file
            })
        }
    }

    const onCrop = () => {
        
        if(proPicture.editor != null)
        {
            const url= proPicture.editor.getImageScaledToCanvas().toDataURL();
            setProPicture({
                userProfilePic : url
            });
        } 
    }

    const onScaleChange = (scaleValueEvent) => {
        const scaleValues = parseFloat(scaleValueEvent.taget.value);
        setProPicture({
            scaleValue : scaleValues
        })
    }

    console.log(formData)
    return (

        <Grid>
            <Grid.Column>
                <Form size='small' onSubmit={handleSubmit(onSubmit)}>
                    <center>
                        <Segment stacked style={{ width: '80%', textAlign: 'left' }}>
                            <h1>Profile Photo</h1>

                            <div>
                                <Grid columns={2} padded style={{ textAlign: 'center' }}>
                                    <Grid.Column>
                                        <List divided relaxed>
                                            <List.Item>
                                                <Button color=''>
                                                    <Icon name='' /> Upload Photo
                                            </Button> &nbsp;&nbsp;&nbsp;

                                                <Button color=''>
                                                    <Icon name='' /> Take Photo
                                                </Button>
                                            </List.Item>
                                        </List>

                                    <input type="file" accept="image/png, image/jpeg" onChange={profileImageChage}  />  
                                    <br/>
                                    <ImageCrop
                                        imageSrc={selectedImage}
                                        setEditorRef={setEditorRef}
                                        onCrop={onCrop}
                                        scaleValue={proPicture.scaleValue}
                                        onScaleChange={onScaleChange}
                                        />

                                    </Grid.Column>
                                    <Grid.Column style={{ textAlign: 'left' }}>
                                        <p>
                                            Tips for good photo
                                        </p>
                                        <Image src={amazingPhoto} size="medium" />
                                        <List>
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                            <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        </List>
                                    </Grid.Column>
                                </Grid>
                            </div>


                            <Button
                                color='teal'
                                fluid
                                size='large'
                                onClick={() => navigation.previous()}
                            >
                                Back
                            </Button>

                            <Button
                                color='teal'
                                fluid
                                size='large'
                                type="submit"
                                onClick={() => navigation.next()}
                            >
                                Next
                    </Button>

                        </Segment>
                    </center>
                </Form>

            </Grid.Column>
        </Grid >
    )

}

export default Registration_Second;
