import React, { useState, useEffect, useReducer,  useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Input, List, Label, Divider, Item, Dropdown, Select, Checkbox
} from 'semantic-ui-react';

import { useForm } from "react-hook-form";
import Timekeeper from 'react-timekeeper';
import { DateInput, TimeInput, DateTimeInput, DatesRangeInput } from 'semantic-ui-calendar-react';

const initialState = {
    AVAILABILITY_DAY: '',
    AVAILABILITY_FROM: '',
    AVAILABILITY_TO:''
}


const reducer = (prevState, { field, value }) => {
    return {
        ...prevState,
        [field] : value
    }
}


const Registration_Fifth = ({ formData, setForm, navigation }) => {

    const {
        COUNSELLOR_TIME_ZONE_CODE,
        COUNSELLOR_AVAILABILITY_DATE,
        COUNSELLOR_AVAILABILITY_TIME_FROM,
        COUNSELLOR_AVAILABILITY,
        COUNSELLOR_AVAILABILITY_TIME_TO
    } = formData;

    const [timings, setTimings] = useState('')

    const [monday, setMonday] = useState([]);
    const [tuesday, setTuesday] = useState({ time: '' });
    const [wednesday, setWednesday] = useState({ time: '' });
    const [thursday, setThursday] = useState({ time: '' });
    const [friday, setFriday] = useState({ time: '' });
    const [saturday, setSaturday] = useState({ time: '' });


    const friendOptions = [
        {
            key: 'Jenny Hess',
            text: 'Jenny Hess',
            value: 'Jenny Hess',
            image: { avatar: true, src: '/images/avatar/small/jenny.jpg' },
        },
        {
            key: 'Elliot Fu',
            text: 'Elliot Fu',
            value: 'Elliot Fu',
            image: { avatar: true, src: '/images/avatar/small/elliot.jpg' },
        },
    ]

    const { handleSubmit, register, errors } = useForm({

    });



    const onSubmit = (data) => {
        navigation.next()
    };

    const Counsellor_TimeZone = [
        {
            title: "Choose Institute",
            name: [
                "Adrenaline Addicts", "Culture Lovers", "Aquatic adventures", "Beach enthusiasts"
            ],
            code: [0, 1, 2, 3]
        }
    ]

    const onTimeChangeHandler = (val) => {
        setMonday([...monday, 
            { 
                Day : 'Monday',
                From : '0',
                End :val.target.value
            }])
    }

    useEffect(() => {
        formData.COUNSELLOR_AVAILABILITY_TIME_FROM = monday;
    }, [monday])


    useEffect(() => {

        if (COUNSELLOR_AVAILABILITY_TIME_FROM)
            setMonday(COUNSELLOR_AVAILABILITY_TIME_FROM)
    }, [])



    const [state, dispatch] = useReducer(reducer, initialState)

    const onChange = e => {
        dispatch({field : e.target.field, value: e.target.value})
    }
    const { AVAILABILITY_DAY, AVAILABILITY_FROM, AVAILABILITY_TO } = state;


    return (

        <Grid>
            <Grid.Column>
                <Form size='small' onSubmit={handleSubmit(onSubmit)}>
                    <center>

                        <h1>Availability</h1>
                        <h2>Set your time zone</h2>

                        <Item.Group>
                            <Item>
                                <Item.Content>
                                    <Form.Group>
                                        <Form.Field className="CustomForm">
                                            <label>Country of origin</label>
                                            <select name="COUNSELLOR_TIME_ZONE_CODE" value={COUNSELLOR_TIME_ZONE_CODE} onChange={setForm} placeholder="choose subject">
                                                {
                                                    Counsellor_TimeZone.map((level) => {
                                                        return (
                                                            <React.Fragment>
                                                                <option disabled selected hidden>{level.title}</option>
                                                                {
                                                                    level.name.map((name, code) => <option value={code}>{name}</option>)
                                                                }
                                                            </React.Fragment>
                                                        )

                                                    })
                                                }
                                            </select>
                                        </Form.Field>
                                    </Form.Group>
                                </Item.Content>
                            </Item>

                        </Item.Group>

                        <h4>Set your Availability</h4><br />

                        <Grid>
                            <Grid.Row>
                                <Grid.Column width={16}>
                                    <center>
                                        <Segment style={{ width: '50%', textAlign: 'center', border:'0px solid transparent'}}>
                                           

                                             <Segment.Group horizontal size="tiny" className="HeadingForAvailability">
                                                <Segment className="customSegment">
                                                <Label color="teal" horizontal>
                                                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Days&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                             </Label>
                                                </Segment>
                                                <Segment className="customSegment">
                                                <Label color="teal" horizontal>
                                                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;From&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                             </Label>
                                                </Segment>
                                                <Segment className="customSegment"> 
                                                <Label color="teal" horizontal>
                                                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;To&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                             </Label>
                                                </Segment>
                                            </Segment.Group>


                                            <Segment.Group horizontal size="tiny" className="HeadingForAvailability">
                                                <Segment className="customSegment">
                                                    <Label style={{display: 'block'}}>
                                                        <Checkbox label='Monday' />

                                                    </Label>
                                                </Segment>
                                                <Segment className="customSegment">
                                                    <input
                                                        type="time"
                                                        step="1"
                                                        value={AVAILABILITY_TO ? AVAILABILITY_TO : formData.COUNSELLOR_AVAILABILITY_TIME_FROM}
                                                        name="COUNSELLOR_AVAILABILITY"
                                                        className="form-control"
                                                        placeholder="Time"
                                                        onChange={onTimeChangeHandler}
                                                    />
                                                </Segment>
                                                <Segment className="customSegment"> 
                                                <input
                                                    type="time"
                                                    step="1"
                                                    value={monday.End ? monday.time : formData.COUNSELLOR_AVAILABILITY_TIME_FROM}
                                                    name="COUNSELLOR_AVAILABILITY"
                                                    className="form-control"
                                                    placeholder="Time"
                                                    onChange={onTimeChangeHandler}
                                                /></Segment>
                                            </Segment.Group>

                                            
                                            <Segment.Group horizontal size="tiny" className="HeadingForAvailability">
                                                <Segment className="customSegment">
                                                    <Label style={{display: 'block'}}>
                                                        <Checkbox label='Monday' />

                                                    </Label>
                                                </Segment>
                                                <Segment className="customSegment">
                                                    <input
                                                        type="time"
                                                        step="1"
                                                        value={monday.time ? monday.time : formData.COUNSELLOR_AVAILABILITY_TIME_FROM}
                                                        name="COUNSELLOR_AVAILABILITY_TIME_FROM"
                                                        className="form-control"
                                                        placeholder="Time"
                                                        onChange={onTimeChangeHandler}
                                                    />
                                                </Segment>
                                                <Segment className="customSegment"> 
                                                <input
                                                    type="time"
                                                    step="1"
                                                    value={monday.time ? monday.time : formData.COUNSELLOR_AVAILABILITY_TIME_FROM}
                                                    name="COUNSELLOR_AVAILABILITY_TIME_FROM"
                                                    className="form-control"
                                                    placeholder="Time"
                                                    onChange={onTimeChangeHandler}
                                                /></Segment>
                                            </Segment.Group>

                                        </Segment>
                                    </center>
                                </Grid.Column>
                            </Grid.Row>
                        </Grid>
                        <br />
                        <Button
                            color='teal'
                            fluid
                            size='large'
                            onClick={() => navigation.previous()}
                        >
                            Back
                            </Button>

                        <Button
                            color='teal'
                            fluid
                            size='large'
                            type="submit"
                            onClick={() => navigation.next()}
                        >
                            Next
                    </Button>

                    </center>
                </Form>

            </Grid.Column>
        </Grid >
    )

