import React, { useState, useEffect, useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Input, List, Label, Divider, Item, Dropdown, Select, Checkbox
} from 'semantic-ui-react';

import { useForm } from "react-hook-form";
import Timekeeper from 'react-timekeeper';

const Registration_Fifth = ({ formData, setForm, navigation }) => {

    const {
        COUNSELLOR_TIME_ZONE_CODE,
        COUNSELLOR_AVAILABILITY_DATE,
        COUNSELLOR_AVAILABILITY_TIME_FROM,
        COUNSELLOR_AVAILABILITY_TIME_TO
    } = formData;

    const [monday, setMonday] = [{ time : '00:00am', display:false, MondayArray:[]}]



    const [timeMon, setTimeMon] = useState('00:00pm')
    const [showTimeMon, setShowTimeMon] = useState(false)

    const [timeTue, setTimeTue] = useState('12:34pm')
    const [showTimeTue, setShowTimeTue] = useState(false)

    const [timeWed, setTimeWed] = useState('12:34pm')
    const [showTimeWed, setShowTimeWed] = useState(false)

    const [timeThu, setTimeThu] = useState('12:34pm')
    const [showTimeThu, setShowTimeThu] = useState(false)

    const [timeFri, setTimeFri] = useState('12:34pm')
    const [showTimeFri, setShowTimeFri] = useState(false)

    const [timeSat, setTimeSat] = useState('12:34pm')
    const [showTimeSat, setShowTimeSat] = useState(false)

    const [timeSun, setTimeSun] = useState('12:34pm')
    const [showTimeSun, setShowTimeSun] = useState(false)


    const friendOptions = [
        {
            key: 'Jenny Hess',
            text: 'Jenny Hess',
            value: 'Jenny Hess',
            image: { avatar: true, src: '/images/avatar/small/jenny.jpg' },
        },
        {
            key: 'Elliot Fu',
            text: 'Elliot Fu',
            value: 'Elliot Fu',
            image: { avatar: true, src: '/images/avatar/small/elliot.jpg' },
        },
    ]

    const { handleSubmit, register, errors } = useForm({

    });


    const onSubmit = (data) => {
        navigation.next()
    };

    const Counsellor_TimeZone = [
        {
            title: "Choose Institute",
            name: [
                "Adrenaline Addicts", "Culture Lovers", "Aquatic adventures", "Beach enthusiasts"
            ],
            code: [0, 1, 2, 3]
        }
    ]

    const onTimeChangeHandler = (val) => {

        // do something with this value

    }

    console.log(formData)

    return (

        <Grid>
            <Grid.Column>
                <Form size='small' onSubmit={handleSubmit(onSubmit)}>
                    <center>
                        
                            <h1>Availability</h1>
                            <h2>Set your time zone</h2>

                            <Item.Group>
                                <Item>
                                    <Item.Content>
                                        <Form.Group>
                                            <Form.Field className="CustomForm">
                                                <label>Country of origin</label>
                                                <select name="COUNSELLOR_TIME_ZONE_CODE" value={COUNSELLOR_TIME_ZONE_CODE} onChange={setForm} placeholder="choose subject">
                                                    {
                                                        Counsellor_TimeZone.map((level) => {
                                                            return (
                                                                <React.Fragment>
                                                                    <option disabled selected hidden>{level.title}</option>
                                                                    {
                                                                        level.name.map((name, code) => <option value={code}>{name}</option>)
                                                                    }
                                                                </React.Fragment>
                                                            )

                                                        })
                                                    }
                                                </select>
                                            </Form.Field>
                                        </Form.Group>
                                    </Item.Content>
                                </Item>

                            </Item.Group>

                            <h4>Set your Availability</h4><br />

                            <Grid>
                                <Grid.Row>
                                    <Grid.Column width={16}>
                                        <center>
                                            <Segment stacked style={{ width: '70%', textAlign: 'left' }}>
                                                <Label color="teal" horizontal>
                                                    Tips for an amazing Photo
                                        </Label>

                                                <List selection>
                                                    <List.Item>
                                                        <Label>
                                                            <Checkbox label='Monday' />
                                                        </Label>
                                                        {showTimeMon &&
                                                            <Timekeeper

                                                                time={timeMon}
                                                                onChange={(newTime) => setMonday({...monday, time : newTime.formatted12})}
                                                                onDoneClick={() => setMonday({display :false})}
                                                                switchToMinuteOnHourSelect
                                                            />
                                                        }
                                                        <span>From {timeMon}</span>
                                                        {!showTimeMon &&
                                                            <button onClick={() => setMonday({...monday, display:true})}>Select Time</button>
                                                        }
                                                    </List.Item>


                                                </List>
                                            </Segment>
                                        </center>
                                    </Grid.Column>
                                </Grid.Row>
                            </Grid>
                            <br/>
                            <Button
                                color='teal'
                                fluid
                                size='large'
                                onClick={() => navigation.previous()}
                            >
                                Back
                            </Button>

                            <Button
                                color='teal'
                                fluid
                                size='large'
                                type="submit"
                                onClick={() => navigation.next()}
                            >
                                Next
                    </Button>

                    </center>
                </Form>

            </Grid.Column>
        </Grid >
    )

}

export default Registration_Fifth;
