import React, { useState, useEffect, useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Divider, List
} from 'semantic-ui-react';
import { useForm } from "react-hook-form";

const Registration_First = ({ formData, setForm, navigation }) => {

    const { firstName } = formData;
    console.log(formData)

    const { handleSubmit, register, errors } = useForm({

    });

    const onSubmit = (data) => {
        navigation.next()
    };


    return (

        <Grid textAlign='left' verticalAlign='middle'>
            <Grid.Column>
                <Form size='small' onSubmit={handleSubmit(onSubmit)}>
                    <Segment stackedstyle={{width:'90%'}}>

                        <Form.Group widths='equal'>
                            <Form.Field>
                                <label>User Input</label>
                                <input
                                    fluid
                                    icon='user'
                                    iconPosition='left'
                                    placeholder='Enter your details'
                                    type='text'
                                    name="firstName"
                                    onChange={setForm}
                                    value={firstName}
                                    ref={register({ validate: firstName => firstName && firstName.length > 3 })}
                                />
                            </Form.Field>
                            {errors.firstName && <p>Username invalid</p>}

                            <Form.Field>
                                <label>User Input</label>
                                <input
                                    fluid
                                    icon='user'
                                    iconPosition='left'
                                    placeholder='Enter your details'
                                    type='text'
                                    name="firstName"
                                    onChange={setForm}
                                    value={firstName}
                                    ref={register({ validate: firstName => firstName && firstName.length > 3 })}
                                />
                            </Form.Field>
                            {errors.firstName && <p>Username invalid</p>}

                            <Form.Field>
                                <label>User Input</label>
                                <input
                                    fluid
                                    icon='user'
                                    iconPosition='left'
                                    placeholder='Enter your details'
                                    type='text'
                                    name="firstName"
                                    onChange={setForm}
                                    value={firstName}
                                    ref={register({ validate: firstName => firstName && firstName.length > 3 })}
                                />
                            </Form.Field>
                            {errors.firstName && <p>Username invalid</p>}

                        </Form.Group>

                        <Button
                            color='teal'
                            fluid
                            size='large'
                            type="submit"
                        >
                            Next
                        </Button>
                    </Segment>
                </Form>

            </Grid.Column>
        </Grid>
    )

}

export default Registration_First;
