import GetWidthOfComponent from './GetWidthOfComponent'
import React, { Component, useState, useEffect } from 'react'
import _Header from '../SecondaryComponents/Desktop/_Header'
import _NavigationItems from '../SecondaryComponents/Desktop/_NavigationItems'
import _MovieGenres from '../SecondaryComponents/Desktop/_MovieGenres'
import axios from 'axios'
import _Movie from '../../src/Store/_Movie'

import { Responsive, Grid } from 'semantic-ui-react'
import _BrowseCategories from '../SecondaryComponents/Desktop/_BrowseCategories'
import _MovieGenresList from '../SecondaryComponents/Desktop/_MovieGenresList'

const DesktopComponent = ({ children }) => {

  const [input, setInput] = useState('');
  return (
    <Responsive getWidth={GetWidthOfComponent} minWidth={Responsive.onlyTablet.minWidth}>

      <Grid>
        <_Header />
        <_NavigationItems />
      </Grid>

      <_BrowseCategories />

      <_MovieGenres onSearchHandler={e => setInput(e.target.value)} />

      {input ? <div>{movieList}</div>: <_MovieGenresList />}


      {/* {children} */}

    </Responsive >
  )
}

export default DesktopComponent;
