import React, { useReducer } from 'react';


const initStateListOfMovies = {
    listOfMovies: [],
    backdrop_path: '',
    id: 0,
    poster_path: '',
    original_name: '',
    overview: ''
}

const reducer = (prevState, action) => {
    alert(prevState);
    return {
        overview: action.value
    }
}


const _Redux = () => {

    const [newState, dispatch] = useReducer(reducer, initStateListOfMovies)

    return (
        <div>
            <input type='text' name={newState.overview} onChange={e => dispatch({ value: e.target.value })} />
            {newState.overview}
        </div>
    )
}

export default _Redux;
