import React, { useState, useEffect, useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Divider, List, Dropdown, Select
} from 'semantic-ui-react';
import { useForm } from "react-hook-form";
import Dropzone from 'react-dropzone';
import { useStep } from 'react-hooks-helper';

const Registration_Second = ({ formData, setForm, navigation }) => {

    const { COUNSELLOR_PHOTO } = formData;
    const [onDrop, setOnDrop] = useState();
    const [DisplayErrorImageSize, setDisplayErrorImageSize] = useState("");
    const [ImageErrors, setImageErrors] = useState([]);

    console.log(formData)

    const { handleSubmit, register, errors } = useForm({

    });

    const onSubmit = (data) => {
        navigation.next()
    };

    const handleOnDrop = (files, rejectedFiles) => {
        const currentRejectedFile = rejectedFiles[0].file
        if(rejectedFiles)
        {
            setImageErrors(rejectedFiles[0].errors);
        }
        // const currentRejectedFileType =  currentRejectedFile.type
        // const currentRejectedFileSize = currentRejectedFile.size
        console.log("current reject files", currentRejectedFile)
        console.log("currentRejectedFileErrors",currentRejectedFileErrors)
        // if(currentRejectedFileSize > maxSizeofImage)
        // {
        //     alert("file too big");
        //     setDisplayErrorImageSize("File is larger than 5MB")
        // }
    }

    const maxSizeofImage = 5e+6; //bytes

    return (

        <Grid>
            <Grid.Column>
                <Form size='small' onSubmit={handleSubmit(onSubmit)}>
                    <center>

                        <Dropzone onDrop={handleOnDrop} multiple={false} accept="image/*" maxSize={maxSizeofImage}>
                            {({ getRootProps }) => (
                                <div {...getRootProps()}>
                                    <p>Drop files here, or click to select files</p>
                                </div>
                            )}
                        </Dropzone>

                        <span>{DisplayErrorImageSize}</span>

                        <Segment stacked style={{ width: '90%', textAlign: 'left' }}>
                            <Form.Group widths='equal'>
                                <Form.Field className="CustomForm">
                                    <label>Counsellor Photo</label>
                                    <input
                                        fluid
                                        icon='user'
                                        iconPosition='left'
                                        placeholder='Enter your details'
                                        type='text'
                                        name="COUNSELLOR_FIRST_NAME"
                                        onChange={setForm}
                                        value={COUNSELLOR_PHOTO}
                                    />
                                </Form.Field>

                            </Form.Group>


                            <Button
                                color='teal'
                                fluid
                                size='large'
                                onClick={() => navigation.previous()}
                            >
                                Back
                    </Button>

                            <Button
                                color='teal'
                                fluid
                                size='large'
                                type="submit"
                                onClick={() => navigation.next()}
                            >
                                Next
                    </Button>

                        </Segment>
                    </center>
                </Form>

            </Grid.Column>
        </Grid >
    )

}

export default Registration_Second;
