import React, { useState } from 'react';
import { Grid, Image, List, Divider } from 'semantic-ui-react'

const _SearchMovieListDisplay = (props) => {

    const [movieList, setMovielist] = useState(true);

    const backgroundStyle = {
        backgroundSize: "cover",
        backgroundImage: `url(https://image.tmdb.org/t/p/original/${props.movie.backdrop_path || props.movie.poster_path})`,
    }

    return (
        <React.Fragment>
            {movieList ?
                <List bulleted horizontal>
                    <center className="customImageDisplay">
                        <Image onClick={props.movieDetails, () => setMovielist(false)} src={props.movieImage} className="sizeOfNewSearchMovie" />
                    </center>
                </List>
                :
                <div  style={backgroundStyle}>
                    <div>
                        <h1>
                            {props.movie.title || props.movie.name}
                        </h1>
                        <p>
                            <span>
                                Rating: {props.movie.vote_average * 10}%{" "}
                            </span>
                        Release date: {props.movie.release_date || props.movie.first_air_date}
                        Runtime: {props.movie.runtime || props.movie.episode_run_time}m
                    </p>
                        <p>
                            {props.movie.number_of_episodes ? " Episodes: " + props.movie.number_of_episodes : ""}
                            {props.movie.number_of_seasons ? " Seasons: " + props.movie.number_of_seasons : ""}
                        </p>
                        <p>{props.movie.overview}</p>
                        <button>

                            Play
                    </button>
                        <button>

                            My List
                    </button>
                    </div>
                </div>
            }
        </React.Fragment >
    )
}

export default _SearchMovieListDisplay;
