import React, { useState, useEffect, useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Divider, List, Dropdown, Select
} from 'semantic-ui-react';
import { useForm } from "react-hook-form";
import { useStep } from 'react-hooks-helper';
import demoPicture from '../../../Static/Images/demoPicture.png'
import amazingPhoto from '../../../Static/Images/amazingPhoto.png'
import 'react-image-crop/dist/ReactCrop.css'
import ReactCrop from 'react-image-crop';

const Registration_Second = ({ formData, setForm, navigation }) => {

    const { COUNSELLOR_PHOTO } = formData;
    const [src, selectFile] = useState(null);
    const [image, setImage] = useState(null);
    const [crop, setCrop] = useState({ aspect: 1 / 1 });
    const [result, setResult] = useState(null);


    const { handleSubmit, register, errors } = useForm({

    });

    const onSubmit = (data) => {

        navigation.next()
    };

    const handleFileChange = e => {
        selectFile(URL.createObjectURL(e.target.files[0]))
    }

    function getCroppedImg() {
        const canvas = document.createElement('canvas');
        const scaleX = image.naturalWidth / image.width;
        const scaleY = image.naturalHeight / image.height;
        canvas.width = crop.width;
        canvas.height = crop.height;
        const ctx = canvas.getContext('2d');

        ctx.drawImage(
            image,
            crop.x * scaleX,
            crop.y * scaleY,
            crop.width * scaleX,
            crop.height * scaleY,
            0,
            0,
            crop.width,
            crop.height,
        );

        const base64Image = canvas.toDataURL('image/jpeg');

        setResult(base64Image);
    }


    console.log(formData)
    return (

        <Grid>
            <Grid.Column>
                <center>
                    <Segment stacked style={{ width: '80%', textAlign: 'left' }}>
                        <h1>Profile Photo</h1>

                        <div>
                            <Grid columns={2} padded style={{ textAlign: 'center' }}>
                                <Grid.Column>
                                    <List divided relaxed>
                                        <List.Item>
                                            <label class="custom-file-input" for="Upload" >
                                            </label>
                                            <input
                                                type="file"
                                                onChange={handleFileChange}
                                                id="Upload"
                                                name="_photos"
                                                accept="image/*"
                                                style={"visibility: hidden"}
                                            />
                                        </List.Item>
                                    </List>

                                    {src && <div className="col-6">
                                        <ReactCrop
                                            src={src}
                                            onImageLoaded={setImage}
                                            crop={crop}
                                            onChange={setCrop}
                                        />
                                        <button className="btn btn-danger" onClick={getCroppedImg}>Crop Image</button>
                                    </div>
                                    }


                                </Grid.Column>
                                <Grid.Column style={{ textAlign: 'left' }}>
                                    <p>
                                        Tips for good photo
                                        </p>
                                    {
                                        result && <div>
                                            <img src={result} alt="cropped image" className="img-fluid" />
                                        </div>
                                    }
                                </Grid.Column>
                            </Grid>
                            <Grid>
                                <Grid.Column style={{ textAlign: 'left' }}>
                                    <p>
                                        Tips for good photo
                                        </p>
                                    <Image src={amazingPhoto} size="medium" />
                                    <List>
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                        <List.Item icon='hand point right' content='Smile and look at the camera' />
                                    </List>
                                </Grid.Column>
                            </Grid>
                        </div>

                        <Form size='small' onSubmit={handleSubmit(onSubmit)}>
                            <Button
                                color='teal'
                                fluid
                                size='large'
                                onClick={() => navigation.previous()}
                            >
                                Back
                            </Button>

                            <Button
                                color='teal'
                                fluid
                                size='large'
                                type="submit"
                                onClick={() => navigation.next()}
                            >
                                Next
                    </Button>
                        </Form>
                    </Segment>
                </center>

            </Grid.Column>
        </Grid >
    )

}

export default Registration_Second;
