import React, { useReducer } from 'react';


const initialState = {
    name: '',
    age: '',
}

const reducer = (prevState, { field, value }) => {
    return {
        ...prevState,
        [field]: value
    }
}

const _Redux = () => {

    const [state, dispatch] = useReducer(reducer, initialState)
    const onChange = e => {
        alert(e)
        dispatch({ field: e.target.field, value: e.target.value })
    }
    const { name, age } = state;
    return (
        <div>
            <input type='text' name="name" onChange={onChange} />
            <input type='text' name="age" onChange={onChange} />
            {age} } {name}
        </div>
    )
}

export default _Redux;
