import React, { useReducer, useEffect, useState } from 'react';
import _AxiosInstance from '../Store/_AxiosInstance'
import DisplayAllGeresOfMovies from '../Containers/DisplayAllGeresOfMovies';
import { Grid, Image } from 'semantic-ui-react'
import SearchGenresResult from '../Containers/SearchGenresResult'



const initState = {
    movieList: [],
}

const reducder = (prevState, action) => {
    switch (action.type) {
        case 'FETCH_TRENDING':
            return {
                movieList: action.payload.data.results,
            };

        case 'FETCH_HORROW':
            return {
                movieList: action.payload.data.results,
            };

        default:
            return prevState;
    }
}

const _Redux = props => {

    const [stateTrending, dispatchTrending] = useReducer(reducder, initState);
    const [seletedGenreMovieId, setSeletedGenreMovieId] = useState({ type: '', id: '', name: '' });
    const [movie, setMovie] = useState([]);
    const [value, setValue] = useState(false);

    useEffect(() => {
        _AxiosInstance.get('/trending/all/week?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US')
            .then(data => {
                dispatchTrending({ type: props.FetchType, payload: data })
            })
            .catch(err => {

            })
    }, []);

    const selectedMovie = movie => {
        setSeletedGenreMovieId({
            id: movie.id,
            type: movie.media_type,
            name: movie.title || movie.name
        });
        setValue(true);
    }

    useEffect(() => {
        let movies = stateTrending.movieList.map((movie) => {
            let movieImageUrl = 'https://image.tmdb.org/t/p/w500/' + movie.backdrop_path;

            if (movieImageUrl !== null) {
                const movieComponent = (
                    <DisplayAllGeresOfMovies
                        movieDetials={() => selectedMovie(movie)}
                        key={movie.id}
                        movie={movie}
                    />
                );
                return movieComponent;
            }
        });

        setMovie(movies);

    }, [stateTrending.movieList]);

    return (
        <React.Fragment>
            <Grid padded>
                <Grid.Row>
                    <Grid.Column textAlign='center'>
                        {movie}
                    </Grid.Column>
                </Grid.Row>
            </Grid>
            { value ? <SearchGenresResult Ids={seletedGenreMovieId} /> : " " }
        </React.Fragment >

    )
}

export default _Redux;
