import React, { useReducer, useEffect } from 'react';
import axios from 'axios'

const initState = {
    movieList : [],
    movieOverview : {}
}

const reducder = (prevState, action) => {
    switch (action.type) {
        case 'FETCH_NETFLIX_ORIGINALS':
            return { ...prevState, 
                movieList : action.payload.data.results 
            };

        default:
            return prevState;
    }
}

const _Redux = () => {

    const [state, dispatch] = useReducer(reducder, initState);

    useEffect(() => {
