import React, { useState, useEffect } from 'react';
import { Header, Icon, Segment, Grid, Modal, Button, Embed, Image, Rating, Divider } from 'semantic-ui-react';
import _HeaderMovieCast from './_HeaderMovieCast';
import ReactPlayer from 'react-player'


const _HeaderMovieDetails = ({url}) => {
    const [open, setOpen] = useState(false);
    const [dimmer, setDimmer] = useState('');

    const show = (dimmer) => {
        setDimmer(dimmer);
        setOpen(true);
    }

    const close = () => {
        setOpen(false);
    }
    console.log("aacxxxxxxxxxxxxxxxxxxxxxxxx", url)
    return (
        <React.Fragment>
            <Modal trigger={
                <Button icon labelPosition='left' style={{ backgroundColor: `transparent`, border:'1px solid grey'}} onClick={() => show('blurring')}>
                    <Icon name='play' color='green' />
                    <span style={{ color: 'white' }}>Play
                    </span>
                </Button>} dimmer={dimmer} open={open} onClose={() => close()} closeIcon>
                <Modal.Content style={{ backgroundColor: 'transparent !important' }}>
                    <Modal.Description>
                        <Segment color='transparent' inverted>
                            <ReactPlayer url={url}
                                playing
                                loop
                                width='100%'
                                controls
                            />
                        </Segment>
                    </Modal.Description>
                </Modal.Content>
            </Modal>
        </React.Fragment >
    )
}

export default _HeaderMovieDetails;
