import React, { useState, useEffect, useReducer } from 'react';
import { useForm, useStep } from 'react-hooks-helper';
import Registration_First from './_Registration_First';
import Registration_Second from './_Registration_Second';
import Registration_Summary from './_Registration_Second';


const defaultData = {
    firstName: "",
    secondName: "",
    age: ""
}

const steps = [
    { id: 'names' },
    { id: 'address' },
    { id: 'summary' }
]

const RegistrationMultiStepForm = () => {

    const [formData, setForm] = useForm(defaultData);

    const { step, navigation } = useStep({
        steps,
        initialStep: 0
    })

    const props = { formData, setForm, navigation }

    switch (step.id) {
        case 'names':
            return <Registration_First {...props} />

        case 'address':
            return <Registration_Second {...props} />

        case 'summary':
            return <Registration_Summary {...props} />
    }

    console.log(step);

    return (
        <React.Fragment>
            {/* <Registration_First /> */}
        </React.Fragment>

    )

}

export default RegistrationMultiStepForm;
