import React, { useState, useEffect } from 'react';
import abc from '../../Static/Images/finalBg.png'
import { Segment, Grid, Image, Header, Icon, Input, Container, List }
    from 'semantic-ui-react'
import _RegistrationModal from './_RegistrationModal';
import counsellor from '../../Static/Images/FinalBannerImage.png';

const _Header = () => {
    const [value, setValue] = useState(false);

    const showModal = () => {
        setValue(true);
    }

    const CloseModal = () => {
        setValue(false);
    }

    return (
        <React.Fragment>
            <Grid.Row className="customGridPadding">
                <Grid.Column width={16}
                    style={{
                        width: '100%',
                        minHeight: 520,
                        maxHeight: 520,
                        backgroundImage: `url(${abc})`,
                        // backgroundImage: `linear-gradient(360deg, rgba(1,73,88,0) 30%, rgba(0,212,255,1) 100%)`,
                        display: 'inline-block',
                        backgroundSize: 'cover',
                    }}>
                    <Container clearing style={{ backgroundColor: 'transparent', width: "100%", padding: '30px 50px 0px 50px' }}>
                        <div style={{ float: 'left' }}>
                            <List bulleted horizontal >
                                <List.Item as='a' style={{ color: 'black' }}>Find a Counsellor</List.Item>
                                <List.Item as='a' style={{ color: 'black' }}>Become a Counsellor</List.Item>
                            </List>
                        </div>
                        <div style={{ float: 'right' }}>
                            <Icon.Group size='large'>
                                <Icon name='question circle' style={{ color: 'white' }}/>
                            </Icon.Group>

                            <span onClick={() => showModal()} className="LogIn" >
                                <Icon.Group size='large' style={{ marginLeft: '20px' }}>
                                    <Icon name='sign-in' />
                                </Icon.Group>&nbsp;&nbsp;Log In
                            </span>
                        </div>
                    </Container>

                    <Container clearing style={{ 
                        backgroundColor: 'transparent',
                         width: "100%", 
                         marginTop:'5rem',
                         padding:'0rem 7rem 0rem 7rem'
                         }}>
                        <div style={{ float: 'left',paddingTop: '7rem' }}>
                        <Input
                                    size='big'
                                    action={{
                                        color: 'teal',
                                        labelPosition: 'left',
                                        icon: 'address book outline',
                                        content: 'Find a course',
                                    }}
                                    actionPosition='right'
                                    placeholder='Search your courses..'
                                    defaultValue=''
                                />
                        </div>
                        <div style={{ float: 'right' }}>
                        <Image src={counsellor} size='large' />
                        </div>
                    </Container>

                </Grid.Column >

            </Grid.Row >

                {value ? <_RegistrationModal onCloseModal={() => CloseModal()} /> : " "}

        </React.Fragment >
    )
}

export default _Header;
