import React, { useState } from 'react';
import { Icon, Segment, Modal, Button } from 'semantic-ui-react';
import ReactPlayer from 'react-player'


const _HeaderMovieDetails = ({url}) => {
    const [open, setOpen] = useState(false);
    const [dimmer, setDimmer] = useState();

    const show = d => {
        setDimmer(d);
        setOpen(true);
    }

    const close = () => {
        setOpen(false);
    }
    return (
        <React.Fragment>
            <Modal trigger={
                <Button icon labelPosition='left' style={{ backgroundColor: `transparent`, border:'1px solid grey'}} onClick={() => show('blurring')}>
                    <Icon name='play' color='green' />
                    <span style={{ color: 'white' }}>Play
                    </span>
                </Button>} dimmer={dimmer} open={open} onClose={() => close()} closeIcon>
                <Modal.Content style={{ backgroundColor: 'transparent !important' }}>
                    <Modal.Description>
                        <Segment inverted>
                            <ReactPlayer url={url}
                                playing
                                loop
                                width='100%'
                                controls
                                origin = 'https://demonlp.000webhostapp.com/'
                            />
                        </Segment>
                    </Modal.Description>
                </Modal.Content>
            </Modal>
        </React.Fragment >
    )
}

export default _HeaderMovieDetails;
