import React, { useReducer, useEffect } from 'react';
import _AxiosInstance from '../Store/_AxiosInstance'
import _MovieGenresListTrending from '../SecondaryComponents/Desktop/_MovieGenresListTrending';
import { Grid, Image } from 'semantic-ui-react'
import _MovieGenresListHorror from '../SecondaryComponents/Desktop/_MovieGenresListHorror';

const initState = {
    movieList: [],
    movieOverview: {}
}

const reducder = (prevState, action) => {
    switch (action.type) {
        case 'FETCH_TRENDING':
            return {
                ...prevState,
                movieList: action.payload.data.results
            };

        default:
            return prevState;
    }
}

const _Redux = () => {

    const [state, dispatch] = useReducer(reducder, initState);
    let movies, movies1;

    useEffect(() => {
        _AxiosInstance.get('/trending/all/week?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US')
            .then(data => {
                dispatch({ type: 'FETCH_TRENDING', payload: data })
            })
            .catch(err => {

            })
    }, []);

    if (state.movieList) {
        movies = getMovieRows(state.movieList, '/trending/all/week?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US')
    }

    if (state.movieList) {
        movies1 = getMovieRows1(state.movieList, '/trending/all/week?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US')
    }

    return (
        <React.Fragment>
            <Grid className="GridRow">
                <Grid.Column className="GridColumn">
                    <div className="scrollmenuImage">
                        {
                            <center className="customImageDisplay">
                               {movies}
                            </center>
                        }
                    </div>
                </Grid.Column>
            </Grid>
            <Grid className="GridRow">
                <Grid.Column className="GridColumn">
                    <div className="scrollmenuImageBigger">
                        {
                            <center className="customImageDisplay">
                               {movies1}
                            </center>
                        }
                    </div>
                </Grid.Column>
            </Grid>
            <Grid className="GridRow">
                <Grid.Column className="GridColumn">
                    <div className="scrollmenuImage">
                        {
                            <center className="customImageDisplay">
                               {movies}
                            </center>
                        }
                    </div>
                </Grid.Column>
            </Grid>
        </React.Fragment>

    )
}

const getMovieRows = (movies, url) => {
    const movieRow = movies.map((movie) => {
        let movieImageUrl = 'https://image.tmdb.org/t/p/w500/' + movie.backdrop_path;

        if (movie.poster_path && movie.backdrop_path !== null) {
            const movieComponent = (
                <_MovieGenresListTrending
                    //movieDetials = {selectedMovie(movie)}
                    key={movie.id}
                    movie={movie}
                />
            );

            return movieComponent;
        }
    });

    return movieRow;
}

const getMovieRows1 = (movies, url) => {
    const movieRow = movies.map((movie) => {
        let movieImageUrl = 'https://image.tmdb.org/t/p/w500/' + movie.backdrop_path;

        if (movie.poster_path && movie.backdrop_path !== null) {
            const movieComponent = (
                <_MovieGenresListHorror
                    //movieDetials = {selectedMovie(movie)}
                    key={movie.id}
                    movie={movie}
                />
            );

            return movieComponent;
        }
    });

    return movieRow;
}

export default _Redux;
