import React, { useReducer } from 'react';


const initStateListOfMovies = {
    listOfMovies: [],
    backdrop_path: '',
    id: 0,
    poster_path: '',
    original_name: '',
    overview: ''
}

const reducer = (prevState, {field, value}) => {

    return {
        [field] : value
    }
}


const _Redux = () => {

    const [newState, dispatch] = useReducer(reducer, initStateListOfMovies)
    const onChange = e => {
        dispatch({field : e.target.field, value: e.target.value})
    }
    const { overview, backdrop_path } = newState;
    return (
        <div>
            <input type='text' name="overview" value={overview} onChange={e => dispatch({ field : e.target.field, value: e.target.value })} />
            <input type='text' name="backdrop_path" value={backdrop_path} onChange={e => dispatch({ field: e.target.field, value: e.target.value })} />
            {overview} | {backdrop_path}
        </div>
    )
}

export default _Redux;
