import React, { useState, useEffect, useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Loader, Divider, Item, Dropdown, Select, Checkbox
} from 'semantic-ui-react';

import { useForm } from "react-hook-form";
import Timekeeper from 'react-timekeeper';


const Registration_Fifth = ({ formData, setForm, navigation }) => {

    const {
        COUNSELLOR_TIME_ZONE_CODE,
        COUNSELLOR_AVAILABILITY_DATE,
        COUNSELLOR_AVAILABILITY_TIME_FROM,
        COUNSELLOR_AVAILABILITY_TIME_TO
    } = formData;

    const [time, setTime] = useState('12:34pm')
    const [showTime, setShowTime] = useState(false)

    const [timeEnd, setTimeEnd] = useState('12:34pm')
    const [showTimeEnd, setShowTimeEnd] = useState(false)

    const { handleSubmit, register, errors } = useForm({

    });


    const onSubmit = (data) => {
        navigation.next()
    };

    const Counsellor_TimeZone = [
        {
            title: "Choose Institute",
            name: [
                "Adrenaline Addicts", "Culture Lovers", "Aquatic adventures", "Beach enthusiasts"
            ],
            code: [0, 1, 2, 3]
        }
    ]

    console.log(formData)

    return (

        <Grid>
            <Grid.Column>
                <Form size='small' onSubmit={handleSubmit(onSubmit)}>
                    <center>
                        <Segment stacked style={{ width: '80%', textAlign: 'left' }}>
                            <h1>Availability</h1>
                            <h2>Set your time zone</h2>

                            <Item.Group>
                                <Item>
                                    <Item.Content>
                                        <Form.Group>
                                            <Form.Field className="CustomForm">
                                                <label>Country of origin</label>
                                                <select name="COUNSELLOR_TIME_ZONE_CODE" value={COUNSELLOR_TIME_ZONE_CODE} onChange={setForm} placeholder="choose subject">
                                                    {
                                                        Counsellor_TimeZone.map((level) => {
                                                            return (
                                                                <React.Fragment>
                                                                    <option disabled selected hidden>{level.title}</option>
                                                                    {
                                                                        level.name.map((name, code) => <option value={code}>{name}</option>)
                                                                    }
                                                                </React.Fragment>
                                                            )

                                                        })
                                                    }
                                                </select>
                                            </Form.Field>
                                        </Form.Group>
                                    </Item.Content>
                                </Item>

                            </Item.Group>

                            <h4>Set your Availability</h4><br />



                            <Segment.Group key={size} size={size}>
                                <Segment>It's a {size} segment</Segment>
                                <Segment>And it's a {size} segment, too</Segment>
                            </Segment.Group>
                            <Checkbox label='Monday' />

                            <Grid>
                                <Grid.Row>
                                    <Grid.Column width={16} style={{ textAlign: 'center' }}>
                                        <Segment stacked style={{ width: '80%' }}>
                                            <Label color='green' horizontal>
                                                Tips for an amazing Photo
                                            </Label>
                                            <br />
                                            <br />
                                            <Image src={amazingPhoto} size="medium" />
                                            <br />
                                            <div>
                                                <Segment color='olive' size="mini">Smile and look at the camera</Segment>
                                                <Segment color='teal' size="mini">Smile and look at the camera</Segment>
                                                <Segment color='violet' size="mini">Smile and look at the camera</Segment>
                                                <Segment color='red' size="mini">Smile and look at the camera</Segment>
                                                <Segment color='yellow' size="mini">Smile and look at the camera</Segment>
                                            </div>
                                        </Segment>
                                    </Grid.Column>
                                </Grid.Row>
                            </Grid>

                            {showTime &&
                                <Timekeeper
                                    time={time}
                                    onChange={(newTime) => setTime(newTime.formatted12)}
                                    onDoneClick={() => setShowTime(false)}
                                    switchToMinuteOnHourSelect
                                />
                            }
                            <span>Start {time}</span>
                            {!showTime &&
                                <button onClick={() => setShowTime(true)}>Select Time</button>
                            }

                            {showTimeEnd &&
                                <Timekeeper
                                    time={timeEnd}
                                    onChange={(newTime) => setTimeEnd(newTime.formatted12)}
                                    onDoneClick={() => setShowTimeEnd(false)}
                                    switchToMinuteOnHourSelect
                                />
                            }
                            <span>---{timeEnd}</span>
                            {!showTimeEnd &&
                                <button onClick={() => setShowTimeEnd(true)}>Select Time</button>
                            }

                        <Button
                            color='teal'
                            fluid
                            size='large'
                            onClick={() => navigation.previous()}
                        >
                            Back
                            </Button>

                        <Button
                            color='teal'
                            fluid
                            size='large'
                            type="submit"
                            onClick={() => navigation.next()}
                        >
                            Next
                    </Button>

                        </Segment>
                    </center>
                </Form>

            </Grid.Column>
        </Grid >
    )

}

export default Registration_Fifth;
