import React, { useReducer, useEffect, useState } from 'react';
import _AxiosInstance from '../Store/_AxiosInstance'
import DisplayAllGeresOfMovies from '../Containers/DisplayAllGeresOfMovies';
import { Grid, Image } from 'semantic-ui-react'
import _SearchMovieList from '../SecondaryComponents/Desktop/_SearchMovieList'


const initState = {
    movieList: [],
}

const reducder = (prevState, action) => {
    switch (action.type) {
        case 'FETCH_TRENDING':
            return {
                movieList: action.payload.data.results,
            };

        case 'FETCH_HORROW':
            return {
                movieList: action.payload.data.results,
            };

        default:
            return prevState;
    }
}

const _Redux = props => {

    const [stateTrending, dispatchTrending] = useReducer(reducder, initState);
    const [movieOverview, setMovieOverview] = useState({});
    const [inputValue, setInputValue] = useState(' ');

    let movies;

    useEffect(() => {
        _AxiosInstance.get('/trending/all/week?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US')
            .then(data => {
                dispatchTrending({ type: props.FetchType, payload: data })
            })
            .catch(err => {

            })
    }, []);

    const selectedMovie = movieId => {
        setInputValue(movieId);
    }

    if (stateTrending.movieList) {
        movies = stateTrending.movieList.map((movie) => {
            let movieImageUrl = 'https://image.tmdb.org/t/p/w500/' + movie.backdrop_path;

            if (movieImageUrl !== null) {
                const movieComponent = (
                    <DisplayAllGeresOfMovies
                        movieDetials={() => selectedMovie()}
                        key={movie.id}
                        movie={movie}
                    />
                );
                return movieComponent;
            }
        });
    }

    

    return (
        <React.Fragment>
            <Grid className="GridRow">
                <Grid.Column className="GridColumn">
                    <div className="scrollmenuImage">
                        {
                            <center className="customImageDisplay">
                                {movies}
                            </center>
                        }
                    </div>
                </Grid.Column>
            </Grid>
            {inputValue ? <_SearchMovieList movieGenreSelected={movieOverview} /> : " "}
        </React.Fragment>

    )
}

export default _Redux;
