import GetWidthOfComponent from './GetWidthOfComponent'
import React, { useState, useEffect } from 'react'
import _NavigationItems from '../SecondaryComponents/Desktop/_NavigationItems'
import _MovieGenres from '../SecondaryComponents/Desktop/_MovieGenres'
import axios from 'axios'
import _SearchMovieList from '../SecondaryComponents/Desktop/_SearchMovieList'

import { Responsive } from 'semantic-ui-react'
import _SearchMovieListDisplay from '../SecondaryComponents/Desktop/_SearchMovieListDisplay'
import FetchAllGenresOfMovies from '../Containers/FetchAllGenresofMovies'

const DesktopComponent = ({ children }) => {

  const [inputValue, setInputValue] = useState();
  const [movieList, setMovieList] = useState([]);
  const [movieOverview, setMovieOverview] = useState({});

  useEffect(() => {
    const url = `https://api.themoviedb.org/3/search/multi?api_key=028e8ea4ca7d9164d085c9238979098b&language=en-US&include_adult=false&query=${inputValue}`;
    if (inputValue != "") {
      axios.get(url)
        .then(res => {
          const results = res.data.results;
          let movieImageUrl;
          let movieRows = [];

          results.forEach((movie) => {
            if (movie.poster_path !== null && movie.media_type !== "person") {
              movieImageUrl = "https://image.tmdb.org/t/p/w500" + movie.poster_path;

              const movieComponent = <_SearchMovieListDisplay
                movieDetials={() => selectMovieHandler(movie)}
                key={movie.id}
                movieImage={movieImageUrl}
                movie={movie}
              />
              movieRows.push(movieComponent);
            }
          })

          setMovieList(movieRows);

        }).catch(error => {
          console.log("request 1 errror", error);
        });
    }

    return () => {
      setMovieList([]);
    }

  }, [inputValue]);

  const selectMovieHandler = movie => {
    let url;
    if (movie.media_type === "movie") {
      const movieId = movie.id;
      url = `https://api.themoviedb.org/3/movie/${movieId}?api_key=028e8ea4ca7d9164d085c9238979098b&append_to_response=videos`;

    } else if (movie.media_type === "tv") {
      const tvId = movie.id
      url = `https://api.themoviedb.org/3/tv/${tvId}?api_key=028e8ea4ca7d9164d085c9238979098b&append_to_response=videos`;
    }

    axios.get(url)
      .then(res => {
        const movieData = res.data;
        setMovieOverview(movieData);

      }).catch(error => {
        console.log(error);
      });
  }

  const goHeee = () => {
    setMovieOverview({});
    setInputValue();

  }

  return (
    <Responsive getWidth={GetWidthOfComponent} minWidth={Responsive.onlyTablet.minWidth}>

      <_NavigationItems />

      <_MovieGenres valueOff={valueOf} onSearchHandler={e => setInputValue(e.target.value)} />

      {inputValue ? <_SearchMovieList movie={movieOverview} listOfMovies={movieList} onSearchHandler={e => setInputValue(e.target.value)} searchInputValue={inputValue} onCloseModal={() => goHeee()} /> : " "}

      {children}

    </Responsive >
  )

}


export default DesktopComponent;
