import React, { useState, useEffect } from 'react';
import _HeaderMovieDetails from './_HeaderMovieDetails';
import vk from "../../Static/Images/vikings.png";
import _HeaderMovieCast from './_HeaderMovieCast';
import shaw from '../../Static/Images/shaw.png';
import narcos from '../../Static/Images/narcos.png'

import {
    Button, Container, Menu, Responsive, Segment,
    Visibility, Grid, Divider, Image, Header, Search, Icon, Dropdown, Label
}
    from 'semantic-ui-react'

const _Header = () => {

    const [bgColor, setBgColor] = useState('#111111');
    const [width, setWidth] = useState(window.innerWidth);

    useEffect(() => {
        const handleResize = () => setWidth(window.innerWidth);
        window.addEventListener('resize', handleResize);
        if (width < 1000)
            setBgColor('transparent')
        else
            setBgColor('#111111')
            
        return () => {
            window.removeEventListener('resize', handleResize);
        };
    });



    return (
        <React.Fragment>
            <Grid.Row className="customGridPadding">
                <Grid.Column width={2}>
                    <Segment style={{ minHeight: 400, maxHeight: 400, padding: '25px' }}>
                        <Menu vertical text >
                            <Image src={shaw} className="shawProperties"></Image>
                            <Divider hidden />
                            <Menu.Item as="a">Theatres</Menu.Item>
                            <Menu.Item as="a">Promotions</Menu.Item>
                            <Menu.Item as="a">Showtimes</Menu.Item>
                            <Menu.Item as="a">Bookings</Menu.Item>
                            <Menu.Item as="a">
                                <Icon name="twitter" color='teal' />
                                <Icon name="instagram" color='pink' />
                                <Icon name="facebook f" color='blue' />
                                <Icon name="youtube play" color='red' />
                            </Menu.Item>
                            <Divider hidden />
                        </Menu>
                    </Segment>
                </Grid.Column>

                <Grid.Column width={14}>
                    <Grid>
                        <Grid.Row>
                            <Grid.Column width={4} style={{ padding: '20px' }} className="headerImageBoxShadow">
                                <Segment style={{ minHeight: 400, maxHeight: 400, backgroundColor: 'transparent' }} verticalAlign='middle'>

                                    <Segment style={{ minHeight: 300, maxHeight: 300, backgroundColor: `${bgColor}` }} verticalAlign='middle'>
                                        <Header color="green" style={{ fontSize: '3.5em', fontFamilty: '', fontWeight: 'bold', padding: '1rem' }}>
                                            Narcos
                                    </Header>
                                        <p style={{ margin: '1rem' }} >
                                            <_HeaderMovieDetails url="https://www.youtube.com/watch?v=AGv_F9hpQ-w" />
                                        </p>
                                        <Responsive as='p' minWidth={1000} className="customPara" style={{ marginTop: '2rem' }}>
                                            A gritty chronicle of the war against Colombia's infamously violent and powerful drug cartels.
                                    </Responsive>
                                    </Segment>
                                </Segment>
                            </Grid.Column >
                            <Grid.Column width={12}>
                                <Image src={narcos} fluid style={{ minHeight: 425, maxHeight: 425, zIndex: -1 }} />
                            </Grid.Column>
                        </Grid.Row>
                    </Grid>
                </Grid.Column>
            </Grid.Row>
        </React.Fragment>
    )
}

export default _Header;
