import React, { useState, useEffect, useContext } from 'react';
import {
    Header, Icon, Form, Image, Message,
    Segment, Grid, Modal, Search, Button, Dimmer, Label, Loader, Divider, List, Dropdown, Select
} from 'semantic-ui-react';
import { useForm } from "react-hook-form";
import { useStep } from 'react-hooks-helper';
import demoPicture from '../../../Static/Images/demoPicture.png'
import amazingPhoto from '../../../Static/Images/amazingPhoto.png'
import 'react-image-crop/dist/ReactCrop.css'
import ReactCrop from 'react-image-crop';

const Registration_Fourth = ({ formData, setForm, navigation }) => {

    const { COUNSELLOR_PHOTO, COUNSELLOR_VIDEO_URL } = formData;
    const [src, selectFile] = useState(null);
    const [image, setImage] = useState(null);
    const [crop, setCrop] = useState({ aspect: 1 / 1 });
    const [result, setResult] = useState(null);



    const { handleSubmit, register, errors } = useForm({

    });

    const onSubmit = (data) => {

        navigation.next()
    };

    const handleFileChange = e => {
        selectFile(URL.createObjectURL(e.target.files[0]))
    }

    function getCroppedImg() {
        const canvas = document.createElement('canvas');
        const scaleX = image.naturalWidth / image.width;
        const scaleY = image.naturalHeight / image.height;
        canvas.width = crop.width;
        canvas.height = crop.height;
        const ctx = canvas.getContext('2d');

        ctx.drawImage(
            image,
            crop.x * scaleX,
            crop.y * scaleY,
            crop.width * scaleX,
            crop.height * scaleY,
            0,
            0,
            crop.width,
            crop.height,
        );

        const base64Image = canvas.toDataURL('image/jpeg');

        setResult(base64Image);

    }


    const Cropped = () => {
        formData.COUNSELLOR_PHOTO = result;
    }

    console.log(result);

    return (

        <Grid>
            <Grid.Column>
                <center>
                    <Segment stacked style={{ width: '80%', textAlign: 'left' }}>
                        <h2>Video</h2>
                        <center>
                            <Grid>
                                <Grid.Row>
                                    <Grid.Column width={8}>
                                        <Segment stacked style={{ width: '100%' }}>
                                            <div style={{ width: '100%', textAlign: 'left' }}>
                                                <Label color='blue' horizontal>
                                                    Upload your video
                                            </Label>
                                            </div>
                                            <br />
                                            <Form.Field className="CustomForm">
                                                <label>First name</label>
                                                <input
                                                    fluid
                                                    icon='user'
                                                    iconPosition='left'
                                                    placeholder='Enter your details'
                                                    type='text'
                                                    name="COUNSELLOR_VIDEO_URL"
                                                    onChange={setForm}
                                                    value={COUNSELLOR_VIDEO_URL}
                                                />
                                            </Form.Field>
                                            <Form.Field className="CustomForm">
                                    <label>First name</label>
                                    <input
                                        fluid
                                        icon='user'
                                        iconPosition='left'
                                        placeholder='Enter your details'
                                        type='text'
                                        name="COUNSELLOR_FIRST_NAME"
                                        onChange={setForm}
                                        value={COUNSELLOR_FIRST_NAME}
                                        ref={register({ validate: COUNSELLOR_FIRST_NAME => COUNSELLOR_FIRST_NAME && COUNSELLOR_FIRST_NAME.length > 3 })}
                                    />
                                    {errors.COUNSELLOR_FIRST_NAME && <p className="customError">Username invalid</p>}
                                </Form.Field>
                                        </Segment>
                                    </Grid.Column>
                                    <Grid.Column width={8} style={{ textAlign: 'left' }}>
                                        <Segment stacked style={{ width: '100%' }}>
                                            <Label color='green' horizontal>
                                                Tips for great videos
                                            </Label>
                                            <br />
                                            <br />
                                            <div>
                                                <Segment color='olive' size="mini">Smile and look at the camera</Segment>
                                                <Segment color='teal' size="mini">Smile and look at the camera</Segment>
                                                <Segment color='violet' size="mini">Smile and look at the camera</Segment>
                                                <Segment color='red' size="mini">Smile and look at the camera</Segment>
                                                <Segment color='yellow' size="mini">Smile and look at the camera</Segment>
                                            </div>
                                        </Segment>
                                    </Grid.Column>
                                </Grid.Row>
                            </Grid>
                        </center>
                        <br /><br />
                        <Form size='small' onSubmit={handleSubmit(onSubmit)}>

                            <Button
                                color='teal'
                                fluid
                                size='large'
                                onClick={() => navigation.previous()}
                            >
                                Back
                            </Button>
                            <br />
                            <Button
                                color='teal'
                                fluid
                                size='large'
                                type="submit"
                                onClick={() => navigation.next()}
                            >
                                Next
                    </Button>
                        </Form>
                    </Segment>
                </center>

            </Grid.Column>
        </Grid >
    )

}

export default Registration_Fourth;
